INSERT INTO `myya_site_info` VALUES (8, 'system_info', '系统以老中医诊疗医案为基础针对其诊疗资料进行管理。包括医案资料管理，如医案标题、中西医诊断、治则治法、方剂名、用药、就诊资料等，并对其信息化管理；数据查询与统计分析，实现数据多维查询，如以老中医、中西医病名、治法、方剂、用药等信息为基础单位，查询及统计相关数据信息，并对名医用药规律有一定的研究。', 'other', '系统简介');
