INSERT INTO `myya_ya` VALUES (1, '张磊涤浊散结法治疗眩晕', 1, '刘某', '32', '教师', '女', '19980818', '间断性头晕4个月，左睾丸肿硬2个月', ' 间断性头晕，测血压：210/120mmHg，服心痛定片，2片/次，一日三次，复方降压片，2片/次，一日三次，血压降至130/95mmHg,同时伴有心前区不适，心电图示：心肌缺血。近2个月左睾丸（附睾）肿硬，会阴部憋胀，无红热现象，口干甚，口苦，夜间耳鸣，大小便正常，阴囊汗出', '头晕，阴肿，口干，口苦，耳鸣，汗出', '眩晕', '原发性高血压,附睾-睾丸炎', '中医内科', ' 6剂,1日1剂，水煎服。', '涤浊散结法', '自拟方', '川楝子10g,荔枝核10g,橘核10g,龙胆草10g,泽泻10g,大黄3g,制附子10g,山楂核15g,甘草6g', '忌辛辣油腻，生活规律', '', '', '', '', '四 诊 ', '四 诊 ', '', '20190504/e0f811751c928af0acf080b62c8fab3d.png', '20190504/df2bd4210a891642704fed5fbe68befc.mp4', '', 52);
INSERT INTO `myya_ya` VALUES (2, '张磊温补脾阳，攻下冷积法治疗腹胀', 1, '祁某', '54', '工人', '女', '19990603', '胃胀发凉8年。', '8年前服治疗类风湿中药（具体药物不详）后，出现胃胀，胃部发凉，似有凉石头，偶有胃隐痛，坐久头懵恶心，服用“附子理中丸”、“补中益气丸”治疗无明显改善。伴有口干舌燥，夜寐多梦，食欲一般，大便溏，一日3-4次，小便正常。', '胃胀，胃凉，口干，寐差，便溏', '腹胀', '慢性胃炎', '中医内科', '6剂,1日1剂 ,水煎服。', '温补脾阳，攻下冷积法', '温脾汤', '肉桂10g,制附子10g,干姜15g,厚朴10g,大黄10g（后下）炙甘草6g', '慎生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 22);
INSERT INTO `myya_ya` VALUES (3, '张磊清热利湿，活血通络法治疗水肿', 1, '张某', '53', '农民', '女', '19990629', '双下肢浮肿发热疼痛2月余。', '双下肢指凹性浮肿，皮肤色红有热感，双胫部出现小疙瘩，疼痛，按之痛重，双膝关节肿大疼痛，活动受限，膝关节内发热，但受凉后疼痛重，大便干结2-3日1次，小便黄，月经色黑暗，带下多色黄，口干，双肘关节沉困。', '浮肿，关节痛，便干，尿黄，带黄，口干', '水肿', '结节性血管炎', '中医内科', '6剂,1日1剂 ,水煎服。', '清热利湿，活血通络法', '化瘀通络方', '丝瓜络30g,路路通15g,木瓜30g,生薏苡仁30g,忍冬藤20g,通草10g,黄柏10g,炒苍术,10g,海桐皮30g,连翘12g赤小豆30g,大黄（后下）6g,陈皮10g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `myya_ya` VALUES (4, ' 张磊清胃导滞法治疗胃痛', 1, '张某', '42', NULL, '女', '19980514', '晚饭后胃脘疼痛8年余。', '患者8年来不吃晚饭，因吃晚饭则胃中不适，渐加重，不吃晚饭则胃不痛。现少食即痛，嗳气有伤食味，口干渴，早、午饭无不适。左乳房时有跳痛发胀，大小便正常，月经后错7天，白带适中。', '胃痛，口干，口渴，乳胀，月经后期', '胃痛', '胃炎', '中医内科', '6剂,1日1剂 ,水煎服', '清胃导滞法', '清胃散加味', '升麻6g,黄连6g,当归10g,生地20g,丹皮10g,生石膏15g,赤芍10g,炒麦芽15g,内金10g,甘草6g', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11);
INSERT INTO `myya_ya` VALUES (5, '张磊清上温下法治疗鹅口疮', 1, '禹某', '27', NULL, '男', '0', '反复性口腔溃疡30年，再发10天。', '30年来反复发作性口腔溃疡，咽干口苦，时有咽痛舌尖疼痛，食欲正常，睡眠一般，大便溏日1次，小便正常。', '口疮，咽干，口苦，咽痛，舌痛，便溏', '鹅口疮', '复发性口腔溃疡。', '中医内科', ' 12剂,1日1剂 ,水煎服。', '清上温下法', '三黄二姜一附汤。', '黄芩10g,黄连10g,黄柏10g,干姜10g,僵蚕10g,制附子10g', ' 忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (6, '张磊清心泻胃法治疗伸舌症', 1, '张某', '59', NULL, '男', '19990910', '舌头伸出口外难易缩回已七天。', '近4年来，每年8、9月份舌头伸出口外难易缩回，不服药不能自愈，心烦急躁，口苦，舌体发硬胀大，言语及饮食受限，大便干，小便正常。口服安宫牛黄丸有效。', '舌肿，心烦，急躁，口苦，便干', '伸舌症', '舌炎', '中医内科', '6剂,1日1剂,水煎服。', '清心泻胃法', '凉膈散', '栀子10g,连翘12g,黄芩10g,薄荷10g(后下),竹叶10g,大黄10g（后下）,芒硝10g（后下）,桃仁12g,赤芍15g,胆南星10g,水牛角30g,黄连10g,甘草6g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 3);
INSERT INTO `myya_ya` VALUES (7, ' 张磊滋补肝肾，引火归源法治疗鹅口疮', 1, '高某', '38', NULL, '男', '19991214', '口唇内周围溃疡28年。', '患者自幼年开始，口唇内周围溃疡色白不断，无诊出。现眼胀有热感。眼球有压痛。口干不欲饮。食欲可，多食则胃瞋胀，失眠多梦。健忘头痛，项强不舒。大便时干时溏，小便有解不尽感。易上火易汗出，早泄。', '口疮，眼胀，口干，失眠，多梦，健忘，项强，便干，汗出，早泄', '鹅口疮', '口腔粘膜溃疡,', '中医内科', '6剂,1日1剂 ,水煎服', '滋补肝肾，引火归源法', '加减金匮肾气丸', '熟地10g,山茱萸10g,生山药15g,牡丹皮10g,泽泻10g,茯苓10g,肉桂3g,制附子3g,白芍10g,当归10g,元参30g,砂仁6g（后下）', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (8, '张磊化饮降气法治疗哮喘', 1, '胡某', '27', NULL, '女', '0', '发作性咳嗽哮喘10年，再发10天。', '患者对花粉油漆及刺激性气味过敏而哮喘发作10年。近因受异味而咳嗽胸闷气喘，痰粘难咯，夏季重，冬季轻。使用哮喘气雾剂可缓解。缓解后如常。大小便正常。说话多声哑。', '咳嗽，哮喘，胸闷，咯痰', '哮喘', '过敏性哮喘', '中医内科', '6剂,1日1剂 ,水煎服', '化饮降气法', '射干麻黄汤,', '炙麻黄6g,杏仁10g,甘草10g,蝉衣6g,白僵蚕10g,炒苏子10g,当归10g,白果10g,炙紫苑10g,白前10g,炙枇杷叶10g,射干10g,诃子10g,北沙参10g,天冬10g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (9, ' 张磊调和营卫，和解少阳法治疗痹证', 1, '余某', '36', NULL, '女', '0', '游走性四肢及颈部肿痛1年余。', '不明原因，近1年来出现四肢及颈部游走性局限性肿胀疼痛，肿胀面积大小不等如核桃或红枣大小，此起彼伏，皮色如常，与情志及天气变化有关，食欲及睡眠可，大小便正常。', '肢痛,颈痛', '痹证', '神经性疼痛', '中医内科', '6剂,1日1剂,水煎服。', '调和营卫，和解少阳法', '柴胡桂枝汤加味', '桂枝10g,白芍10g,柴胡10g,黄芩10g,半夏10g,党参10g,生龙骨30g,生牡蛎30g,炙甘草6g,生姜3片,大枣3枚', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (10, ' 张磊滋阴降火法治疗血证', 1, '王某', '50', NULL, '女', '19000101', '口腔内粘膜渗血50天。', ' 1999年4月出现胸痛背痛，按“冠心病”治疗，给与低分子右旋糖酐、脉络宁针等输液4周，胸背痛症状消失，但50天前出现四肢无力头晕，口腔内粘膜渗血，昼轻夜重，口干不渴，时有口苦噫气口咸，口腔不痛，无牙龈出血，大小便正常。', '口疮,口干,口苦,噫气,口咸', '血证', '口腔炎', '中医内科', ' 6剂,1日1剂,水煎服。', '滋阴降火法', '甘露饮合大补阴丸', '熟地10g,生地10g,天冬10g,麦冬10g,枇杷叶10g,黄芩10g,炒枳壳10g,石斛15g,黄柏10g,知母10g,甘草6g,龟板10g', '勿食辛辣，调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `myya_ya` VALUES (11, '张磊清利湿热，泌请别浊法治疗尿浊', 1, '付某', '51', NULL, '女', '19980922', '尿浊半年。', '近半年来尿白浊，尿频，无尿热尿痛，腰酸腰痛时有眼睑浮肿。头胀，大便正常。', '尿浊，尿频，浮肿，头胀', '尿浊', '待查', '中医内科', '6剂,1日1剂 ,水煎服。', '清利湿热，泌请别浊法', '程氏萆薢分清饮', '莲子15g,竹叶10g,木通6g,生地20g,萆薢15g,连翘12g,赤小豆30g,生薏苡仁30g,黄柏10g,甘草6g,瞿麦20g', '忌辛辣油腻，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (12, '张磊疏利三焦，化湿行水法治疗水肿，郁证', 1, '韦某', '38', NULL, '女', '19971212', '全身浮肿20余年。', '20年前产后因劳累过度出现全身及面目浮肿，每因劳累或情志不畅时病情加重。双下肢呈指凹性水肿。四肢郁胀，头胀头晕。1995年发现血压高，180-150/100-90mmHg,服用降压药物具体不详，腹胀。小便多且尿急，心情急躁。腰痛，带下多色白，无口干口苦，大便正常。', '浮肿,腹胀,尿频,尿急,急躁,腰痛,白带', '水肿，郁证', '功能性水肿', '中医内科', '6剂,1日1剂 ,水煎服。', '疏利三焦，化湿行水法', '达郁汤化裁', '炒白术10g,炒苍术10g,炒薏苡仁30g,炒枳壳10g,炒枳实10g,姜黄10g,陈皮10g,青皮10g,云苓10g,猪苓10g,木瓜15g', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `myya_ya` VALUES (13, ' 张磊调和营卫，养血熄风法治疗风瘙痒', 1, '徐某', '63', NULL, '女', '19990423', '全身皮肤瘙痒40余天。', '40天前行剖腹产术，产后输液（药物不详）3天，出现全身皮肤瘙痒。昼轻夜重，受热后加重，无丘疹，无口干口苦，大小便正常，左乳房胀痛有肿块如枣核大。', '瘙痒,乳胀,乳痛', '风瘙痒', '皮肤过敏', '中医内科', '14剂,1日1剂,水煎服。', '调和营卫，养血熄风法', '桂枝汤', '桂枝10g,白芍10g,制首乌30g,川芎10g,荆芥10g,白蒺藜30g,炙甘草6g,连翘12g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (14, ' 张磊通络达郁，补气清气法治疗发热', 1, '吕某', '52', NULL, '女', '19970930', '间断性腹部及双下肢发热20余年。', '20年来每到夏秋之交，腹部及双下肢肌肉发热难忍，以膝以下为多。白天无夜间明显，持续1-2个月自行消失，测体温36.6摄氏度。发热甚时小便黄。每年冬季手指及膝关节疼痛。', '腹热,肢热', '发热', '功能性低热', '中医内科', '6剂,1日1剂,水煎服', '通络达郁，补气清气法', '自拟方', '忍冬藤30g,丝瓜络30g,木瓜15g,升麻6g,火麻仁15g,白芍10g,黄芪15g,当归10g,黄柏10g,炒苍术10g,甘草6g,通草3g,陈皮10g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (15, '张磊温经通脉，活血止痛法治疗痹证', 1, '胡某', '52', NULL, '女', '19990601', '左下肢外侧疼痛半年。', '半年前不明原因出现左下肢外侧疼痛，按压左髋关节则左下肢酸痛，左足背发木，劳累后或阴雨天加重。无腰痛，时有眼眶痛及头痛，口干，食欲可，大小便正常。', '肢酸,肢痛,足麻,头痛,口干', '痹证', '1，风湿病？2，高血压病', '中医内科', '3剂,1日1剂 ,水煎服。', '温经通脉，活血止痛法', '当归四逆汤,活络效灵丹', '当归10g,桂枝10g,白芍10g,通草6g,细辛3g,丹参30g,制乳香10g,制没药10g,山萸肉10g,知母10g,川牛膝15g,制川乌6g,甘草3g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (16, '张磊益气养血活络法治疗痹证', 1, '赵某', '32', NULL, '女', '19970509', '左半身麻木疼痛、发凉7年。', '7年前因受凉出现左侧肢体外侧发凉，麻木疼痛，阴雨天加重，四肢关节无畸形，月经正常，大小便正常。', '肢麻', '痹证', '类风湿？', '中医内科', '6剂,1日1剂,水煎服。 ', '益气养血活络法', '黄芪桂枝五物汤合四物汤', '生黄芪20g,桂枝10g,白芍10g ,熟地10g,当归10g,川芎6g ,细辛3g,通草6g, 生姜3片,大枣3枚为引', '慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (17, '张磊温阳化气,降浊法治疗眩晕,水肿', 1, '李某', '43', NULL, '男', '19981224', '阵发性头晕恶心呕吐30余年，再发3个月。', '患者有头晕恶心呕吐病史30年，每年冬季易发作，夏季较少出现。自98年9月份以来，病情发作，呕吐物为胃内容物，甚至为水液，目眩，使用“盐酸培他定”、“脑活素”、“维脑路通”等药乏效，双下肢浮肿，双手发麻，面部虚浮晄白，形寒怕冷，劳则头晕，口干口苦，食欲一般，晕甚则视物旋转，有欲倒之势。', '头晕,恶心,呕吐,手麻,面白,形寒,畏冷,头晕,口干,口苦', '眩晕,水肿', '甲状腺功能低下？', '中医内科', '6剂,1日1剂。水煎服', '温阳化气,降浊法', '真武汤合泽泻汤', '炒白术10g,茯苓10g,制附子10g,白芍10g,泽泻15g,生龙骨30g（先煎）,生牡蛎30g（先煎）,生姜9g(为引)', '畅情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 4);
INSERT INTO `myya_ya` VALUES (18, '张磊涤浊解毒法治疗饮证', 1, '尹某', '52', NULL, '男', '20050620', '咳嗽右胸痛2月余。', '2004年7月发现右甲状腺癌，在北京某医院行部分切除术，近2个月出现咳嗽，吐白色泡沫样痰，量多，右胸疼痛，活动后胸闷，自汗多，黎明时恶寒，食欲可，大小便正常。', '咳嗽，胸痛，咯痰，自汗，恶寒', '饮证', '甲状腺癌， 胸腔积液？', '中医内科', '25剂,1日1剂,水煎服。', '涤浊解毒法', '涤浊汤加味', '苇根30g,冬瓜仁30g,生薏苡仁30g,桃仁10g,桔梗15g,猪苓30g,雄黄0.2g（冲）,元胡15g,白芥子10g,蚤休10克,生地10g,茯苓15g,甘草10g', '畅情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (19, ' 张磊化痰祛湿通络法治疗水肿', 1, '董某', '29', NULL, '男', '19990504', '面部及双手浮肿4个月', ' 不明原因出现面部及双手浮肿郁胀，早晨轻下午重，口干微苦，大小便正常，双下肢无浮肿现象。', '浮肿,口干,口苦', '水肿', '高血压,浮肿待查。', '中医内科', '12剂,1日1剂,水煎服', '化痰祛湿通络法', '加味保和丸', '炒神曲15g,炒麦芽15g,炒山楂15g,茯苓12g,半夏10g,陈皮10g,炒卜子10g,连翘10g,生薏苡仁30g,地肤子15g,通草6g,木贼草6g,路路通12g,甘草6g', '畅情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (20, '张磊清热燥湿，和胃止痛法治疗泄泻', 1, '吕某', '33', NULL, '男', '0', '腹泻3年余，加重3月余。', '3年前大便偏干未治疗，渐转为大便溏泻日2-3次，肛门下坠近3个月，大便夹有鲜血及粘液，便前无腹痛，腹部怕冷，胃脘时痛，食后胃胀，噫气，无泛酸烧心，口干口苦，有腥味，小便黄。', '便溏，便血，胃痛，胃胀，嗳气，口干，口苦，尿黄', '泄泻', '溃疡性直肠炎', '中医内科', '6剂,1日1剂,水煎服。', '清热燥湿，和胃止痛法', '济生乌梅丸加减', '炒白术10g,阿胶6g（另炖）,黄芩10g,醋槐花30g,椿皮炭30g,乌梅10g,白僵蚕10g,甘草6g,灶心土30g', '忌食辛辣刺激性食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 3);
INSERT INTO `myya_ya` VALUES (21, '张磊泻肺清热，益气养阴法治疗哮证,咳嗽', 1, ' 樊某', '63', NULL, '男', '0', '咳嗽哮鸣3年。', '3年前受雨淋，之后出现卧床即咳嗽，行走或坐位时不咳。无咽痒，吐白痰量不多，咽中似有异物吼中痰鸣，口干不苦，吸气时易咳，大小便正常，纳眠可，活动或吃饭时汗多。', '咳嗽，痰鸣，口干', '哮证,咳嗽', '过敏性哮喘,', '中医内科', ' 10剂,1日1剂,水煎服。', '泻肺清热，益气养阴法', '生脉散,泻白散,', '党参10g,北沙参15g,麦冬20g,五味子10g,天冬10g,制桑白皮10g,地骨皮10g,干地龙10g,射干10g,桔梗10g,炒苏子6g,当归10g,车前子15g,甘草6g', '调情志，勿劳累，避风寒，忌辛辣刺激食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (22, '张磊疏散少阳风热法治疗耳鸣', 1, '吴某', '53', NULL, '女', '0', '右耳鸣20天', '不明原因出现右耳鸣，开始服用“六味地黄丸”有效，继服乏效。耳鸣如嗡嗡声，伴有头晕恶心、心烦，大小便正常，月经紊乱。白带量多色白，无口干口苦，劳累后有尿失禁现象。', '耳鸣，头晕，恶心，心烦，月经紊乱，白带多，尿失禁', '耳鸣', '神经性耳鸣', '中医内科', '4剂,1日1剂,水煎服。', '疏散少阳风热法', '小柴胡汤', '柴胡10g,黄芩10g,半夏10g,蝉衣6g,二花10g,连翘10g,夏枯草20g,制香附10g,生龙骨30g（先煎）,生牡蛎30g（先煎）,甘草6g', '忌辛辣。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (23, '张磊涤浊清肝法治疗头懵', 1, '赵某', '28', NULL, '男', '20060109', '头懵3月余。', '无明显诱因，3月来头懵，头胀，以双太阳穴周围明显，耳鸣如蝉，眼涩不肿胀，无头痛头晕，大便不干，小便有时黄，眠差，睡不解乏，身困乏力，纳可。', '头懵，头胀，耳鸣，尿黄，寐差，乏力', '头懵', '高血压,代谢综合症？', '中医内科', '12剂,1日1剂 ,水煎服。', '涤浊清肝法', '自拟方', '白茅根30g,冬瓜仁30g,生薏仁30g,桃仁10g,茯苓10g,制半夏10g,陈皮10g,夏枯草30g,酒黄芩10g,菊花10g（后下）,豨签草30g,谷精草30g,郁金12g ,荷叶15g,蝉衣6g,生甘草6g', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (24, ' 张磊滋补肝肾祛风湿法治疗腰痛', 1, '黄某', '40', NULL, '女', '20060109', '腰痛5年', '患者腰痛五年，有时酸稍凉，与天气变化关系不明显；右耳鸣，有时头晕，月经正常，白带量多而稠有异味，纳眠可，二便调。', '腰痛，耳鸣，头晕，白带多', '腰痛', '1、椎间盘膨出。2、宫颈糜烂', '中医内科', '10剂,1日1剂,水煎服', '滋补肝肾祛风湿法', '独活寄生汤', '独活3g,桑寄生15g,秦艽3g,细辛3g,当归10g,川芎10g,白芍10g,熟地10g,桂枝10g,茯苓10g,炒杜仲10g,川牛膝10g,川断10g,狗脊30g,炒小茴10g,鹿角霜10g,炙甘草6g,菊花10g（后下）', '调情志，勿劳累，避风寒湿。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (25, ' 张磊清肝扶脾法治疗眩晕', 1, '冯某', '55', NULL, '女', '0', '头晕目眩一周。', '患者素有高血压病40年，近一周头晕目眩，恶心不吐，口干口苦夜甚，饮水多，四肢颤抖，心慌，背部凉痛，时行走不便利，双下肢轻度浮肿，纳可，大便溏，便前腹痛，四肢乏力，时烧心。', '头晕，目眩，恶心，口干，口苦，多饮，颤抖，心慌，背凉，浮肿，便溏，腹痛，乏力，烧心', '眩晕', '高血压,', '中医内科', '6剂,1日1剂 ,水煎服。 ', '清肝扶脾法', '自拟方', '天麻10g,钩藤30g（后下）,黄芩10g,炒枳实10g,生龙骨30g（先煎）,生蛎牡30g（先煎）,泽泻15g,通草6g,葛根30g,炒白术10g,炒白扁豆30g,生山药30g,菊花10g（后下）,生甘草6g', '调情志，勿劳累，戒烟酒，忌食辛辣。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (26, ' 张磊平调寒热法治疗泄泻', 1, '卢某', '48', NULL, '女', '0', '腹泻1月余。', '一月前因饮食不节出现腹泻，1日1-5次，大便前小腹痛，便后痛减，纳呆，眠可，干哕，口渴；平素身困乏力，内热大，易感冒。 舌质：舌尖红。', '腹泻，纳呆，干哕，口渴，乏力，易感', '泄泻', '腹泻待查', '中医内科', '6剂,1日1剂,水煎服。', '平调寒热法', '乌梅丸', '黄柏10g,党参10g,肉桂6g,制附子6g,细辛3g,黄连10g,当归10g,川椒6g,乌梅10g,干姜10g,蒲公英30g', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (27, '张磊涤浊利湿健脾法治疗水肿', 1, '张某', '29', NULL, '男', '20051228', '双下肢指凹性水肿1月余。', '1月前出现双下肢指凹性水肿，肿至膝部，活动后加重，休息后稍减轻，全身乏力、酸困；纳眠可，二便调。 舌质： 淡胖。  舌苔： 白腻略滑。', '水肿，乏力', '水肿', '高血压,糖尿病,', '中医内科', '20剂,1日1剂,水煎服', '涤浊利湿健脾法', '自拟方', '白茅根30g,冬瓜仁30g,生薏仁30g,木瓜30g,赤小豆30g,决明子15g,通草6g,鬼箭羽30g,炒苍术30g,苏叶6g（后下）,郁金15g,元参15g', '调情志，忌辛辣刺激食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (28, '张磊清胆解郁和胃法治疗嘈杂', 1, '王某', '26', NULL, '女', '20051212', '胃部烧心泛酸间断发作7年余。', '近7年来，经常出现胃部烧心、泛酸、嗳气，吃甜食后加重；时有右胁不适，右侧肩背痛，口干口苦，不欲饮；夜寐差，多梦，每晚睡3-4个小时，大便偏干，2日1次。耳鸣耳闷。', '烧心，泛酸，嗳气，肩背痛，口干，口苦，眠差，多梦，便干，耳鸣', '嘈杂', '慢性胆囊炎,慢性胃炎？', '中医内科', '10剂,1日1剂,水煎服', '清胆解郁和胃法', '越鞠丸', '川芎10g,炒苍术10g,炒神曲10g,制香附10g,栀子10g,柴胡10g,黄芩10g,蒲公英30g,川楝子10g,元胡10g,清半夏10g', '忌食辛辣刺激性食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (29, '张磊清肝泄热法治疗舌病', 1, '范某', '38', NULL, '女', '20051212', '舌头痛、溃疡时轻时重10年，再发1周余。', ' 10年来经常出现舌糜，近1周再发。现舌头痛、溃疡，鼻干，头痛以头顶为主，月经量少色黯，大便偏干，小便可，眠差，入睡困难，纳可，口干口涩。', '舌糜，鼻干，头痛，月经量少，便干，寐差，口涩', '舌病', '舌溃疡,', '中医内科', '15剂,1日1剂,水煎服', '清肝泄热法', '泻青丸', '龙胆草10g,栀子10g,决明子10g,羌活3g,防风3g,川芎6g,当归10g,生地30g,丹皮10g,生石膏30g,黄芩10g,生甘草6g,车前草30g', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (30, '张磊舒经活络法治疗中风', 1, '陈某', '63', NULL, '女', '20051219', '左侧肢体活动不利3月余。', ' 患者原有“高血压”病史，3月前“脑血栓”后出现左侧肢体活动不利，下肢发软，左手左脚发凉，纳眠可，二便调。左侧上下肢易抽筋。', '抽筋', '中风', '高血压,缺血性脑血管病,', '中医内科', '15剂,1日1剂 ,水煎服', '舒经活络法', '自拟方', '木瓜30g,威灵仙12g,白芍30g,川芎10g,桃仁10g,制南星6g, 红花10g,酒桑枝30g,通草6g,怀牛膝15g,生甘草6g,陈皮10g', '调情志，慎风寒，勿劳作。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (31, ' 张磊益气养阴法治疗哮喘', 1, '杜某', '26', NULL, '女', '20050617', '胸闷气喘3月余。', '哮喘，天冷重，咳嗽，喉中痰鸣，胸闷活动后加重。痰少色白，无心慌胸闷，听力下降，食欲可，大便日2-3次溏薄。小便正常，口不苦，右下肢疼痛', '胸闷，气喘，咳嗽，痰鸣，便溏', '哮喘', '支气管炎', '中医内科', '10剂,1日1剂,水煎服', '益气养阴法', '生脉饮，橘枳姜汤加减', '党参15g,麦冬10g,五味子10g,全瓜蒌10g,炒枳实10g,茯苓12g,陈皮10g,山萸肉12g,补骨脂10g,丹参15g,生姜3片,大枣6枚为引（切开）', '调情志，避风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (32, '张磊清热养阴润便法治疗便秘', 1, '王某', '40', '营业员', '女', '20050624', '大便干结10余年。', '大便干结3-4日天1次，频频欲大便而排不出，胸闷口干，咽中有异物感。时腹胀，食欲可。颈强，双肩关节疼痛，月经后错7-8天，量少色黑（1天），白带色黄量多，小腹疼痛。', '便干，胸闷，口干，咽梗，腹胀，颈强，关节痛，月经后错，白带，腹痛', '便秘', '便秘', '中医内科', '10剂,1日1剂 ,水煎服。', '清热养阴润便法', '凉膈散,', '元参30g,黄芩10g,连翘10g,栀子10g,竹叶10g,大黄10g（后下）,芒硝10g（冲服）,槐角30g,当归20g,生白芍30g,炒枳实10g,生甘草6g', '忌食辛辣刺激性食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (33, '张磊益气养阴，化瘀祛痰法治疗胸痹', 1, '赵某', '41', NULL, '女', '20051111', '胸闷气短心慌20多天', ' 胸闷气短心慌阵作,后背痛，手指发凉，左乳下痛，无胸痛，活动后易发作，口不干不苦，纳眠可，心电图示“冠心病”。胃胀噫气不畅，无烧心泛酸。大便不畅，小便正常', '胸闷，气短，心慌，背痛，指凉，乳痛，胃胀，噫气', '胸痹', '冠心病', '中医内科', '6剂,一日一剂,水煎服', '益气养阴，化瘀祛痰法', '生脉散合丹参饮,', '全瓜蒌30g,薤白10g,丹参30g,檀香3g,砂仁3g,降香6g,炒枳壳10g,党参10g,麦冬10g,五味子10g,炒枣仁20g,茯苓10g,当归6g', '调情志，慎风寒，勿劳作。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (34, ' 张磊泻热通腑法治疗泄泻', 1, '赵某', '42', NULL, '女', '20051019', '泄泻2年余。', '3年前出现便秘，大便干燥，服泻下药后出现泄泻。现症：时常有大便感，便前矢气多，轻微脐周腹痛，大便量少，有排不尽感，腹胀，口干不苦，胃部时有隐痛，食生冷则泄泻。月经正常，白带适中', '矢气，脐痛，便少，腹胀，口干，胃痛', '泄泻', '慢性结肠炎', '中医内科', '6剂,1日1剂,水煎服', '泻热通腑法', '自拟方', '槟榔10g,炒二丑6g,五灵脂10g,黄芩10g,生白芍10g,大黄10g（后下）', '忌食辛辣刺激性食物，戒烟酒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (35, '张磊凉肝清心法治疗崩漏', 1, '郑某', '54', NULL, '女', '0', '月经淋漓不尽半年。', '近半年出现月经淋漓不尽2次，上次服用“止血”中药止，本次服“止血宝胶囊”\"宫血宁”效果不明显。现症：月经淋漓不尽1个月，月经色黑，量少，服“宫血宁”等稍减，劳累后又复发，本次月经前提10余日，二便调，纳眠可', '崩漏', '崩漏', '功能性子宫出血', '中医内科', '6剂,1日1剂,水煎服。', '凉肝清心法', '二至丸加味', '生地炭30g,地榆炭30g,栀子10g,黄芩10g,知母10g,黄柏10g,女贞子15g,旱莲草30g,生甘草6g ', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (36, ' 张磊滋补肝肾，疏肝清心法治疗不寐', 1, '霍某', '39', NULL, '女', '20051128', '失眠15天。', '患者经常失眠，近半月来加重。一周前患肠胃炎，经治已愈。现偶感胃部不适，面部有黄褐色斑，食欲可，经期后错，色量正常。 舌质：舌质红', '寐差，月经后错', '不寐', '失眠', '中医内科', '10剂,1日1剂,水煎服', '滋补肝肾，疏肝清心法', '六味地黄丸', '生地15g,山萸肉10g,生山药15g,泽泻10g,丹皮10g,茯苓10g,柴胡10g,生白芍30g,炒枳实15g,制香附10g,栀子6g,木贼草10g,红花6g,生甘草6g', '忌食辛辣刺激性食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (37, '张磊清热活络和胃法治疗胃脘痛', 1, '赵某', '64', NULL, '女', '20051216', '胃痛、胃热、胃酸3个月', '以前早晨不吃饭，致饥饿时胃痛，近3个月加重。胃痛不胀，胃热烧心，泛酸，噫气，口干不苦，食欲可，大便溏，每日1次，小便正常', '胃痛，胃热，泛酸，噫气，口干，便溏', '胃脘痛', '胃溃疡', '中医内科', '10剂,1日1剂,水煎服', '清热活络和胃法', '自拟方', '白芨10g,蚤休10g,炒枳实10g,蒲公英15g,煅乌贼骨20g,赤石脂20g,元胡10g,佛手3g,炒麦芽15g,生黄芪15g', '忌肥甘厚味。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (38, '张磊养阴涤浊法治疗脑鸣', 1, '徐某', '63', NULL, '男', '20051104', '脑鸣8年', '脑鸣耳鸣活动劳累后加重。口苦，头不晕不痛，睡眠及纳食可，大小便正常，血脂高，甘油血脂1.03，胆固醇5.81，肝功能异常，谷丙154，故草62，总胆红素16.1，无乙肝，服降压药血压130/90mmHg。', '耳鸣，口苦', '脑鸣', '高血压,高血脂症', '中医内科', '20剂,1日1剂 ,水煎服。', '养阴涤浊法', '自拟方', '生白芍30g,生地10g,代赭石15g,生山药30g,怀牛膝30g,丹参30g,郁金15g,炒山楂15g,柏子仁6g,决明子15g,败酱草30g,冬瓜仁30g,生意苡仁30g,茯苓10g,泽泻10g,黄芩10g,生龙骨30g（先煎）,生牡蛎30g（先煎）', '调情志，勿劳累，忌食辛辣。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (39, '张磊化痰清热熄风法治疗眩晕', 1, '曹某', '59', NULL, '男', '20051121', '间断头晕1年多，再发10余日', '1年前早晨起床时出现头晕恶心，走路有踏不实感。检查“椎-基底动脉血流速减低”。季节变换时易出现，伴恶心，每次发作持续6-7秒钟，无耳鸣无眩晕，输“丹参注射液”后减轻，二便调，纳眠可，易急躁。', '头晕,恶心,急躁', '眩晕', '脑供血不足', '中医内科', '10剂,1日1剂,水煎服', '化痰清热熄风法', '半夏白术天麻汤,泽泻汤', '清半夏12g,陈皮10g,茯苓12g,炒白术6g,天麻6g,川芎12g,泽泻15g,酒黄芩10g,连翘10g,夏枯草15g,谷精草30g,荷叶15g,生甘草6g', '慎风寒，忌食辛辣刺激性食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (40, '张磊养阴化痰，清心安神法治疗不寐', 1, '李某', '62', NULL, '女', '20050617', '失眠7年', '入睡困难，能休息3小时，靠药物维持，头不痛，怕冷又怕热，食欲可，无心烦，胃中难受有空感，无烧心泛酸，大小便正常，白带适中，48岁断经后出现失眠，手怕凉东西。', '寐差', '不寐', '失眠', '中医内科', '10剂,1日1剂,水煎服', '养阴化痰，清心安神法', '眠安汤', '生地10g,百合20g,麦冬15g,炒枣仁30g,茯神10g,生白芍20g,小麦30g,竹叶6g,生龙骨30g（包煎）,生牡蛎30g（包煎）,炒枳实10g,丹参15g,桃仁10g,红花10g,生甘6g,大枣10个为引（切开）', '畅情志，清淡饮食', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (41, ' 张磊涤浊法治疗咳嗽', 1, '郑某', '34', NULL, '女', '20050624', '咳嗽胸闷乏力1月余', '2000年1月直肠癌术，化疗2年，04年6月肺部转移，05年3月8日放疗，引起咳嗽，无痰，咽痒有异物感，胸闷不痛（晨3-4点最重），无胸痛，双肺均有转移，无发热，食欲可，睡眠少，乏力明显，上楼则气喘，服我处前诊处方苇根30g,冬瓜仁30g,生意苡仁30g,桔梗15g,桃仁10g,天冬10g,海浮石30g,当归10g,地骨皮10g,桑白皮10g,白僵蚕10g,郁金15g,太子参15g,枸杞子15g,猪苓20g,甘草10g有效。', '咳嗽，胸闷，乏力', '咳嗽', '直肠癌肺转移', '中医内科', '15剂,1日1剂,水煎服', '涤浊法', '涤浊汤', '苇根30g,冬瓜仁30g,生意苡仁30g,桃仁10g,茯苓15g,桔梗15g,黄芩10g,桑白皮10g,地骨皮10g,车前子15g（包煎）,牛蒡子10g,天冬10g,北沙参20g,猪苓20g,炒麦芽20g,生甘草10g', '慎起居，避风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (42, '张磊清胃散火法治疗鹅口疮', 1, '杨某', '60', NULL, '男', '0', '口唇周围及下颌湿疹10余年。', '患者经常口唇周围鼻翼两旁及下颌部出红疹，瘙痒，无渗出。口腔溃疡时作时止，平时易上火，易疲劳，口干不苦，痰多，大小便正常。 舌质： 舌质淡红。', '湿疹，口干', '鹅口疮', '湿疹,复发性口腔溃疡', '中医内科', '6剂,1日1剂,水煎服', '清胃散火法', '清胃散', '升麻10g,黄连6g,当归10g,生地15g,丹皮10g,生石膏20g,元参15g,白僵蚕10g,桔梗10g,甘草6g', '忌食辛辣油腻之品。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (43, '张磊补中散火法治疗内伤发热', 1, '张某', '57', NULL, '女', '19980522', '低热2个月，淋巴结肿大20天', '患者不明原因出现低热，测体温36.5-37.0摄氏度。因患病基础体温为36.0摄氏度，伴全身疼痛，食欲不振，头晕眼花，近20天颌下及颈部淋巴结肿大，颈项疼痛。胃镜示：胆汁返流性胃炎。B超：肝胆脾胰无异常。胸片及心电图无异常发现。大便溏，小便正常。', '低热，身痛，纳差，头晕，眼花，颈痛，便溏', '内伤发热', '颈淋巴结炎,发热待查？', '中医内科', '10剂,1日1剂 ,水煎服。', '补中散火法', '补中益气汤', '党参12g,黄芪15g,炒白术10g,当归6g,陈皮10g,白芍10g,升麻6g,柴胡6g,连翘10g,黄芩10g,炒枳壳6g,炙甘草6g,生姜3片,大枣3枚为引', '忌食辛辣油腻忌烟酒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (44, '张磊健脾化湿法治疗水肿', 1, '寇某', '60', NULL, '女', '0', '双下肢浮肿3个月。', '不明原因出现双下肢浮肿，下午重，早晨轻。四肢酸沉憋胀不舒。体倦乏力，睡眠欠佳，食欲可，口干口渴不欲饮。大便成形，1日2-3次，小便正常，面部浮肿', '浮肿，肢沉，乏力，口干，口渴', '水肿', '功能性水肿', '中医内科', '6剂,1日1剂 ,水煎服', '健脾化湿法', '参苓白术散', '党参10g,炒白术10g,云苓10g,炒山药15g,炒莲肉15g,炒白扁豆15g,羌活3g,防风3g,苏叶3g,木瓜30g,炙甘草6g,炒薏苡仁30g', '忌食太咸食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (45, '张磊健脾化积法治疗胃痛', 1, '崔某', '53', NULL, '男', '19970718', '胃脘痛半年。', '1997年初因饮食过饱致胃脘不舒，饭前隐痛，饭后胀痛，泛酸，精神不振，食量受限，口不干苦，大小便正常，胃镜示：出血性胃底炎', '胃痛，泛酸，纳差', '胃痛', '胃炎', '中医内科', '6剂,1日1剂,水煎服', '健脾化积法', '延年半夏汤', '半夏12g,槟榔6g,鳖甲9g,炒枳壳6g,桔梗3g,前胡6g,党参6g,吴茱萸3g,元胡10g,煅乌贼骨10g,黄连2g,生姜3片为引', '忌食辛辣生硬食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (46, ' 张磊疏肝健脾，清利湿热法治疗乳房胀痛,带下病', 1, '刘某', '48', NULL, '女', '0', '双乳房疼痛7年', '乳房胀痛，经前重，按之不重。月经正常，白带量多，色黄。纳眠可，大小便正常，肩背不舒。', '乳胀，乳痛，白带多，肩背不舒', '乳房胀痛，带下病', '乳腺小叶增生', '中医内科', '20剂,1日1剂 ,水煎服', '疏肝健脾，清利湿热法', '丹栀逍遥散', '柴胡10g,白芍10g,当归10g,炒白术10g,茯苓10g,薄荷3g（后下）,制香附15g,青皮10g,丹皮10g,栀子10g,土茯苓30g,金银花30g,黄柏10g,生甘草6g', '畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (47, ' 张磊疏利法治疗郁胀证', 1, '张某', '65', NULL, '女', '20050715', '双下肢郁胀10余年。', '双下肢郁胀，下午重，活动后轻，早上轻，按之无指凹， 经前重，夜尿正常，手郁胀，全身郁胀，早晨较下午重，白带适中，大便正常，食欲可', '身胀', '郁胀证', '下肢深静脉炎', '中医内科', ' 10剂,1日1剂 ,水煎服。', '疏利法', '疏补相兼方', '炒白术10g,炒苍术10g,茯苓15g,猪苓10g,炒枳壳10g,炒枳实10g,陈皮10g,青皮10g,木瓜30g,生薏苡仁30g,丝瓜络30g（另煎）,泽泻10g,通草6g,连翘10g,赤小豆30g,生甘草3g', '避风寒，防感冒，勿劳累，忌食生冷油腻。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (48, '张磊燮理法治疗泄泻', 1, '王某', '38', NULL, '男', '20050620', '腹泻2年', ' 患者腹泻2年，2-6次/天，时腹胀，易出虚汗，纳可眠可，小便稍黄，性功能差，有时肠鸣', '腹泻，腹胀，多汗，肠鸣', '泄泻', '慢性结肠炎', '中医内科', ' 6剂,1日1剂 ,水煎服', '燮理法', '山前汤（张磊教授经验方）化裁', '生山楂15g,炒山楂15g,生车前子15g（包煎）,炒车前子15g（包煎）,生山药15g,炒山药15g,煨柯子10g,煨肉豆蔻10g,羌活3g,独活3g,炮干姜6g', '忌辛辣油腻，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (49, '张磊化痰开窍法治疗中风', 1, '李某', '52', NULL, '男', '0', '脑出血1年7个月。', '脑出血1年零7个月。现症：烦躁易怒，记忆力减退，自己出去回不了家，呈痴呆状，大便干，双腿乏力，打呼噜，语言不清。 舌质： 舌质暗，舌体胖，有齿痕', '烦躁，易怒，记忆力减退，便干，乏力，语骞', '中风', '脑出血后遗症,', '中医内科', ' 15剂,1日1剂 ,水煎服', '化痰开窍法治', '导痰汤加减', '制半夏12g,陈皮12g,茯苓15g,胆南星10g,石菖蒲15g,郁金15g,黄芩10g,决明子15g,炒枳实15g,芒硝15g（后下）', '忌辛辣油腻，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (50, '张磊健脾化积法治疗胃痛', 1, '徐某', '30', NULL, '男', '20050718', '胃痛胃胀1年', '胃痛胃胀1年，胃中感觉有震动感，夜间口苦，口渴夜甚，身乏力，夜间身痛，二便可', '胃痛，胃胀，口苦，口渴，乏力，身痛', '胃痛', '十二指肠球部溃疡。', '中医内科', '15剂,1日1剂 ,水煎服', '健脾化积法', '自拟方', '白芨10g,蚤休10g,炒枳实10g,煅乌贼骨15g,生黄芪30g,当归10g,槐角30g,杏仁15g,元胡10g,桑叶10g,菊花10g（后下）,黄芩10g,赤芍15g', '忌辛辣油腻，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (51, '张磊化痰散结，涤浊解毒法治疗瘰疬', 1, '孟某', '42', NULL, '男', '20050711', '右腮腺癌手术后1年余。', ' 04年5月12日行“右侧腮腺癌手术”，现颌下有肿块如鸡蛋黄大，质稍硬，右颌下发紧，二便调，纳眠可', '颌肿', '瘰疬', '腮腺癌淋巴转移。', '中医内科', '30剂,1日1剂 ,水煎服', '化痰散结，涤浊解毒法', '消瘰丸,', '元参30g,大贝10g,生牡蛎30g（先煎）,皂刺10g,川芎10g,山慈菇,10g,蚤休10g,白花蛇舌草30g,生甘草6g,蜈蚣1条,炒白芥子10g,清半夏10g,陈皮10g,雄黄0.2g（另包,份2次冲服）', '调情志，勿劳累，避风寒，忌食生冷油腻。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (52, '张磊燮理法治疗郁证', 1, '马某', '57', NULL, '女', '20050801', '乏力嗜睡2年', '2年来经常乏力嗜睡，易疲劳，纳少，二便调，月经正常，白带适中', '乏力，嗜睡，纳差', '郁证', '植物神经功能紊乱。', '中医内科', '10剂,1日1剂 ,水煎服', '燮理法', '自拟方', '生地10g,熟地10g,天冬10g,麦冬10g,竹叶10g,连翘10g,生白芍10g,当归10g,通草6g,忍冬藤10g,丝瓜络10g,炒麦芽15g,生甘草3g,大枣4个为引（切开）', '畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (53, '张磊养阴泻火法治疗不寐', 1, '丁某', '28', NULL, '女', '20050701', '失眠多梦半年', '入睡慢，多梦，食欲欠佳，口干不苦，二便正常。月经正常，白带量多色白', '寐差,多梦,纳差,口干,白带', '不寐', '失眠', '中医内科', '20剂,1日1剂,水煎服', '养阴泻火法', '导赤散', '生地15g,竹叶10g,通草6g,麦冬30g,生龙骨30g（先煎）,生牡蛎30g（先煎）,小麦30g,炒麦芽20g,生甘草6g', ' 畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (54, '张磊疏利法治疗郁证', 1, '屈某', '55', NULL, '女', '0', '左下肢无力、发麻1个月', '患者左下肢无力，发麻，时双下肢郁肿，纳呆，眠差多梦。全身肌肉按之硬，有结节', '肢软，肢麻，纳差，寐差，多梦', '郁证', '功能性水肿', '中医内科', '15剂,1日1剂,水煎服', '疏利法', '化痰通络方', '制半夏10g,陈皮10g,茯苓10g,木瓜30g,威灵仙12g,白芍30g, 通草6g,酒桑枝20g,制南星10g,橘络6g,元胡10g,生甘草6g', '畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (55, '张磊疏肝理脾法治疗郁证', 1, '路某', '36', NULL, '女', '0', '晨起脸、眼睑浮肿，双手足发胀，间断出现3年', '晨起脸、眼睑浮肿，双手足发胀，稍一运动肿胀即消失，间断出现3年，梦多，乏力。月经经期后移，腹痛，怕凉。有时低烧，脾气急躁，易怒。月经前期乳房胀痛，胸闷，小便黄', '浮肿，多梦，乏力，月经后期，腹痛，怕冷，急躁，易怒，胸闷，尿黄', '郁证', '功能性水肿', '中医内科', '7剂,1日1剂 ,水煎服', '疏肝理脾法', '加减丹栀逍遥散', '柴胡10g,白芍10g,当归10g,炒白术10g,茯苓30g,薄荷3g（后下）,制香附12g,丹皮10g,栀子10g,木瓜30g,生薏仁30g,生甘草3g,通草3g', '畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (56, '张磊燥湿祛痰，健脾和胃法治疗眩晕', 1, '张某', '53', NULL, '女', '20050701', '头痛头晕半年余', '半年来头痛头晕，耳鸣，胃脘胀，嘈杂，口干，纳差，眠差，二便调。', '头痛，头晕，耳鸣，胃胀，嘈杂，口干，纳差，寐差', '眩晕', '高血压病', '中医内科', '20剂,1日1剂。每服5剂停1日继服,水煎服', '燥湿祛痰，健脾和胃法', '半夏白术天麻汤', '制半夏10g,陈皮10g,茯苓10g,炒苍术10g,炒白术10g,天麻6g,炒麦芽20g,炒枳实10g,砂仁3g（后下）,木香6g,生甘草3g,小麦30g,炒枣仁15g', ' 畅情志，慎起居', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (57, ' 张磊灵动法治疗胃脘痛', 1, '王某', '54', NULL, '男', '20050921', '后半夜胃痛1年余', '每晚2点多胃痛，嘈杂不适，按揉觉舒，伴烧心，无泛酸，无噫气，不能食生冷，大便有时干，日1次，小便夜频，眠差，纳少，不能食硬食，夜晚口干。近10余日“感冒”，现：咳嗽，吐粘痰，咽痒。', '胃痛，嘈杂，烧心，便干，夜尿频，眠差，纳差，口干，咳嗽，咽痒', '胃脘痛', '慢性红斑性胃炎', '中医内科', '7剂,1日1剂,水煎服', '灵动法', '健脾丸', '党参10g,炒白术6g,茯苓10g,制半夏10g,陈皮6g,砂仁3g（后下）,草果3g,草蔻3g,黄连3g,蚤休10g,元胡6g,煅瓦楞子15g,决明子20g,杏仁10g,生甘草6g', '忌肥甘厚味', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (58, '张磊化痰通络法治疗郁证', 1, '穆某', '31', NULL, '女', '20050812', '右足发木2年', '2年前患足癣，经用“头痛粉”治疗后右足底发木，近一年加重，伴发麻，麻甚至右膝关节，伴颤抖；右半身不舒，有针刺样感觉。口不干不苦。二便调，纳眠可。', '足麻', '郁证', '周围神经炎？', '中医内科', '7剂,1日1剂,水煎服。', '化痰通络法', '导痰汤', '制半夏10g,陈皮10g,茯苓12g,制南星10g,生黄芪20g,通草6g,木瓜30g,天麻6g,威灵仙10g,桂枝10g,生甘草6g,鸡血藤15g ,生姜3片,大枣3枚为引（切开）', '调情志，避风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (59, '张磊清轻泻浊法治疗头痛,带下病', 1, '孙某', '47', '电台技术员', '女', '20050815', '头晕头痛夜间重1年余', '头晕头痛，夜间重，入睡困难；时咳嗽，口臭口不干，小腹时痛，白带色黄有异味，二便调', '头晕，头痛，寐差，咳嗽，口臭，腹痛，带黄', '头痛,带下病', '1，神经性头痛；2，宫颈炎。', '中医内科', ' 6剂,1日1剂 ,水煎服', '清轻泻浊法', '谷青汤加味', '谷精草30g,青葙子15g,决明子10g,蝉蜕6g,桑叶10g,薄荷10g（后下）,菊花10g（后下）,酒黄芩10g,蔓荆子10g,黄柏10g,土茯苓30g,苦参15g,蒲公英30g,车前子10g（包煎）,生甘草10g', ' 调情志，勿劳累，戒烟酒，忌食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (60, '张磊燮理法治疗郁证', 1, '李某', '65', NULL, '男', '0', '胸闷气短5年，乏力半年', ' 胸闷全身无力，腰痛，性生活质量低；时眩晕耳鸣，纳可，眠差。大便1日4次，口臭。', '胸闷，乏力，腰痛，眩晕，耳鸣，寐差，口臭', '郁证', '神经官能症？', '中医内科', '15剂,1日1剂 ,水煎服', '燮理法', '自拟方。', '炒山药30g,山萸肉10g,枸杞子12g,菊花10g（后下）,黄芩10g,炒薏仁30g,竹叶10g,灯心草3g,夏枯草15g,赤芍10g,赤茯苓15g,琥珀3g（另包）,炒杜仲10g,炒麦芽15g,生甘草6g', '调情志，勿劳累。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (61, '张磊清热燥湿，化瘀止血法治疗胃脘痛,脏躁', 1, ' 韩某', '31', NULL, '女', '0', '胃部胀痛30余年，脓血便近1年', '患者30年前开始胃痛，饭后加重，一年前因精神刺激后出现脓血便，日1次，饮食不当时凌晨2时左右出现胃痛，口干，盗汗，头晕，右耳时鸣，右膝、肘、踝关节痛。饮食可，睡眠稍差，易疲劳。', '胃痛，脓血便，口干，盗汗，头晕，耳鸣，关节痛，寐差，疲劳', '胃脘痛、脏躁', '1.胃溃疡 2.溃疡性结肠炎。', '中医内科', '10剂,1日1剂 ,水煎服', '清热燥湿，化瘀止血法', '乌梅丸', '白僵蚕10g,乌梅10g,醋槐花30g,黄芩10g,生白芍15g,防风10g,地榆15g,二花（炒黑）10g', '忌辛辣，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (62, '张磊清散三阳经郁热法治疗郁证', 1, '李某', '66', NULL, '女', '20051209', '双肩部，后背部烧灼样疼2月。', '双肩部及后背部烧灼样，稍疼，气温变时加重，气温低时轻。夜间2点后全身烧灼感，口干，易感冒，怕冷，胃痞满，泛酸，烧心，食欲欠佳，手心热，二便可。', '肩痛，口干，怕冷，易感，胃满，泛酸，烧心，纳差，手心热', '郁证', '神经官能症', '中医内科', '5剂,1日1剂 ,水煎服', '清散三阳经郁热法', '柴葛解肌汤合麻杏薏甘汤', '炙麻黄6g,杏仁10g,生薏仁30g,葛根30g,升麻10g,柴胡10g,羌活10g,防风10g,草果6g,黄芩10g,炙甘草6g', '避风寒，防感冒，勿劳累，忌食生冷油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (63, '张磊化痰清热法治疗眩晕', 1, '赵某', '34', NULL, '女', '20050706', '头晕4月', '患者，05年3月“亚急性颅内出血引流术”。现症：头晕不舒适，动则胸部不舒，纳可，大便日2-3次，乏力，双下肢微郁，目昏。', '头晕,胸闷,乏力,目昏', '眩晕', '脑出血后遗症', '中医内科', '10剂,1日1剂,水煎服', '化痰清热法', '半夏白术天麻汤,', '泽泻12g,茯苓12g天麻10g,钩藤20g（后下）,黄芩10g,决明子20g,生龙骨30g（包煎）,生牡蛎30g（包煎）,生甘草6g,菊花10g（后下）', '调情志，勿劳累，戒烟酒，忌食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (64, ' 张磊活络透邪法治疗痹证', 1, '刘某', '39', NULL, '男', '20051028', '后背部，肩部疼痛2年', ' 后背部脊柱两侧（膀胱经）疼痛，肩疼，感冒，受凉后加重，疼甚连及头枕部疼痛，活动时疼轻，但活动后疼重，睡觉时腰痛明显，肩背亦痛，二便调，眠可，纳佳，说梦话多，已感冒4天，现鼻塞，咽干，身畏寒，觉体内热，曾服“血府逐瘀丸”初显效，后不佳，疼处觉热。', '肩痛，腰痛，鼻塞，咽干，畏寒', '痹证', '风湿病', '中医内科', '6剂,1日1剂 ,水煎服', '活络透邪法', '活络效灵丹', '忍冬藤30g,连翘12g,通草6g,酒桑枝30g,制没药10g,羌活10g,当归10g,丹参20g,生甘草6g,制乳香10g', '避风寒，防感冒，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (65, '张磊燮理法治疗腰痛', 1, ' 李某', '34', NULL, '女', '20050907', '腰部疼痛1年余', ' 患者1年前劳作过程中出现腰痛，曾按“岔气”治疗，效差。现症：腰痛，呈阵发性，多于站立时出现，发作时不能转侧，可自行缓解；纳可，眠差，入睡困难，二便调', '腰痛，寐差', '腰痛', '神经性疼痛？', '中医内科', '10剂,1日1剂 ,水煎服。', '燮理法', '独活寄生汤,', '独活3g,桑寄生30g,秦艽3g,防风3g,细辛3g,当归10g,川芎10g,白芍10g,熟地10g,桂枝10g,茯苓10g,炒杜仲10g,川牛膝10g,木瓜15g,威灵仙10g,麦冬30g,竹叶10g,元胡10g,生甘草6g', '避风寒，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (66, ' 张磊燮理法治疗泄泻', 1, '赵某', '31', NULL, '男', '20050722', '腹泻时轻时重已3年，加重3个月。', '近3个月，大便稀，3-4次/日，食冷物则加重，一般无腹痛，但常觉不适，胃纳可。外院肠镜示慢性结肠炎。曾服中西药，终未获显效。本病发生前，夏季曾吃冷食半个月，夏天较冬天重', '便溏', '泄泻', '慢性结肠炎', '中医内科', '12剂,1日1剂,水煎服', '燮理法', '山前汤（张磊教授经验方）加味', '炒山楂15g,生山楂15g,炒车前子15g（包煎）,生车前子15g（包煎）,炒山药15g,生山药15g,炮干姜10g,', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (67, '张磊温中达郁法治疗胃胀', 1, '杜某', '54', NULL, '女', '20050722', '胃胀1年余', '胃胀不痛，食欲不振时噫气泛酸烧心，胃怕凉，口不苦，乏力，大小便正常，月经正常，白带适中。', '胃胀，噫气，泛酸，烧心，乏力', '胃胀', '慢性胃炎', '中医内科', ' 10剂,1日1剂 ,水煎服', '温中达郁法', '自拟方', '川芎10g,炒苍术10g,炒神曲10g,制香附10g,栀子10g,良姜6g,干姜6g,丹参30g,檀香3g（后下）,砂仁3g（后下）', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (68, '张磊灵动法治疗胸痹', 1, ' 苏某', '45', NULL, '女', '20050907', '左胸痛连及后背痛2年，左右胸痛连及后背2天', '左胸闷连及后背痛2年，服速效救心丸缓解。睡时不痛，近2天右胸也痛，连及后背，痛时头晕、头痛、胸闷，不恶心，口干口苦，纳差，眠可，二便调，月经紊乱，量多色紫暗，白带适中', '胸痛，背痛，头晕，头痛，胸闷，口干，口苦，纳差，月经量多', '胸痹', '冠心病', '中医内科', ' 15剂,1日1剂 ,水煎服', '灵动法', '丹参饮合百合乌药散', '丹参30g,檀香3g（后下）,砂仁3g（后下）,百合30g,乌药10g', '调情志，慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (69, ' 张磊疏肝泻火法治疗经行口糜', 1, ' 刘某', '32', NULL, '女', '20050909', '经前感冒发热口腔溃疡10年', '经前15天发热头痛口腔溃疡，双下肢红斑，月经过后减轻，经前乳房胀痛、小腹胀，乏力，精神不振，经量适中，月经提前3-4天，白带适中，二便调。', '经行口糜，乳胀，乳痛', '经行口糜', '无,多发性口腔溃疡', '中医内科', ' 6剂,1日1剂,水煎服', '疏肝泻火法', '丹栀逍遥散合白虎汤', '柴胡10g,生白芍10g,当归10g,茯苓10g,薄荷3g（后下）,制香附12g,丹皮10g,栀子10g,青皮10g,黄芩10g,元参30g,党参10g,生石膏30g,知母10g,生甘草10g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (70, ' 张磊法治疗眩晕,月经先后无定期', 1, ' 强某', '53', NULL, '女', '20050909', '晨起头懵头皮麻耳闷1年余', '患者1年来晨起10分钟后出现头懵、头皮麻、耳闷，检查后为“特发性耳聋”，诊治效果不佳。服“一清胶囊”后头懵减轻，但头鸣明显。现症：头鸣，睡觉差时加重，进食油腻后头闷，咽部不适，饭后胃胀。月经后错1周，量多，月经前头鸣加重，眠差', '脑鸣，头闷，胃胀，月经后期，寐差', '眩晕,月经先后无定期', '耳聋,月经失调,', '中医内科', '6剂,1日1剂 ,水煎服', NULL, '枳实芍药散合二至丸', '炒枳实15g,生白芍30g,生地10g,麦冬30g,生龙骨30g(先煎),生牡蛎30g(先煎),女贞子15g,旱莲草30g,知母10g,黄柏6g,炒麦芽15g,佛手9g,生甘草6g', '调情志，慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (71, '张磊燮理法治疗泄泻', 1, '武某', '62', NULL, '女', '0', '腹泻20余日', '无明显诱因，腹泻，2-4次/日，溏便，肠鸣，腹痛，大便急，有解不尽感，不畏寒，无胃胀胃痛，耳鸣如蝉，小便可，眠可，纳可', '腹泻，溏便，肠鸣，腹痛，耳鸣', '泄泻', '慢性结肠炎,', '中医内科', '6剂,1日1剂,水煎服', '燮理法', '山前汤（张磊教授经验方）加味', '生山楂15g,炒山楂15g,生车前子15g（包煎）,炒车前子15g（包煎）,葛根10g,黄芩10g,黄连6g,陈皮10g,防风6g,炒白术10g,生白芍10g ', ' 勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (72, '张磊涤浊法治疗郁胀病', 1, '郑某', '38', NULL, '女', '0', '脸变圆变红3年，向心性肥胖3年。', '患者因“脸变圆变红3年，向心性肥胖3年。出现血压升高4个月”诊断为“库欣综合症”，曾在北京协和医院做腹腔镜右肾上腺全切术。现腹大，身上有明显条状红斑，双脚肿明显，头痛，怕热，纳差，食则胀，大便3-4次/日，动则汗出。现未服降压药。', '向心性肥胖，脚肿 ，头痛，怕热，纳差，汗出，便多', '郁胀病', '库欣综合症', '中医内科', '12剂,1日1剂,水煎服', '涤浊法', '涤浊汤加味（经验方）', '白茅根30g,冬瓜仁30g,生薏仁30g,茯苓30g,通草6g,忍冬藤30g,丝瓜络30g（另包）,赤芍30g,连翘15g,赤小豆30g,桃仁10g,炒麦芽20g,炒神曲10g,炒山楂15g,炒白术10g,陈皮10g', '忌辛辣油腻，生活规律。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (73, ' 张磊益气解暑法治疗感冒', 1, '段某', '33', NULL, '女', '19970815', '全身乏力一月余。', '平时易感冒，一月前又因感冒致全身乏力，服西药治疗乏效；现症：全身乏力，头晕，自感全身多处不时发热，但体温不高，食少，小便黄，大便正常，本月曾做药物流产，腰痛，易出汗。', '乏力，头晕，发热，纳差，尿黄，腰痛，汗出', '感冒', '感冒', '中医内科', '3剂,1日1剂 ,水煎服', '益气解暑法', '清暑益气汤', '太子参30g,竹叶10g,麦冬10g,石斛15g,桑叶6g,枸杞子10g,白茅根30g,黄连3g,知母10g,荷叶15g,甘草3g,山萸肉10g,西瓜皮20g,粳米一撮为引', '慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (74, '张磊健脾化湿祛风法治疗腹痛', 1, ' 赵某', '56', NULL, '女', '19980514', '晨起6点钟腹痛欲便，便后痛消2月余', ' 2月前无明显诱因晨6点出现腹痛阵作欲便，便后痛止，此后每于早上6点均出现此症，便后痛消，大便先干后稀，与饮食无关，食欲可，近5天难以入睡，易早醒，白天头痛，精神不振', '腹痛，大便不调，寐差，头痛', '腹痛', '慢性肠炎,', '中医内科', ' 4剂,1日1剂,水煎服', '健脾化湿祛风法', '人参败毒散', '党参10g,云苓12g,川芎6g,羌活6g,独活6g,柴胡6g,前胡6g,炒枳壳10g,桔梗6g,白芍10g,炙甘草6g,生姜3片,大枣3枚（切开）为引', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (75, ' 张磊化痰燥湿，升清降浊法治疗眩晕', 1, '荆某', '48', NULL, '女', '19991203', '阵发性头晕10余年，再发1周', ' 患者有高血压病史10余年，经常头晕，1周前无明显诱因头晕加重，不能平卧，目眩，经用消心痛、维生素E、阿斯匹林、复方丹参注射液、维脑路通等治疗，血压控制在正常范围，但头晕不减，食少，夜寐差，大小便正常', '头晕，寐差', '眩晕', '1.高血压病 2.颈椎病', '中医内科', '6剂,1日1剂 ,水煎服', '化痰燥湿，升清降浊法', '半夏白术天麻汤', '半夏12g,陈皮10g,茯苓10g,炒白术10g,天麻10g,泽泻10g,川芎12g,生龙骨30g（包煎）,生牡蛎30g（包煎）,甘草6g,炒白芥子10g', '调情志，慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (76, '张磊化痰清热法治疗胸痹', 1, '朱某', '47', NULL, '男', '19980814', '心慌胸闷气短5年，加重1年。', '近几年每年冬季易感冒，有心慌胸闷气短现象，98年5月初输液（用药不详）致发热5-6小时，随之出现全身瘫痪，汗出较多，后转铁路中心医院神经科救治，用药后症状缓解，瘫痪恢复正常，但心慌胸闷气短加重，活动后心慌加重，食欲可，睡眠差，每晚休息3-4个小时，饱食后胸闷甚，胃胀，噫气，口干苦，不排气，大便溏日1次，小便黄。', '心慌，胸闷，气短，胃胀，嗳气，口干，口苦，便溏，尿黄，寐差', '胸痹', '冠心病,', '中医内科', '6剂,1日1剂 ,水煎服', '化痰清热法', '温胆汤,', '清半夏10g,陈皮10g,茯苓10g,砂仁10g（另包）,竹茹20g,全瓜蒌10g,薤白10g,炒枣仁15g,党参10g,黄连6g,远志10g,炒白术10g,厚朴10g,炙甘草6g', '调情志，慎风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (77, ' 张磊清热化湿，行气降浊法治疗痹证', 1, '姜某', '55', NULL, '女', '19980717', '间断性右踝外侧红肿胀痛2月余。', '2月前无明显诱因出现右踝外侧红肿胀痛，触之有硬结，发病后双下肢明显怕凉，得温则舒，经服中药3剂后症状改善不明显，伴有右侧肢体不舒，纳眠可，小便正常，大便干，3-4日一次。', '关节痛，便干', '痹证', '痛风', '中医内科', ' 4剂,1日1剂 ,水煎服', '清热化湿，行气降浊法', '鸡鸣散', '木瓜30g,防己12g,橘红12g,吴茱萸12g,苏叶12g（后下）,制川乌10g,肉苁蓉30g,炒火麻仁30g,生薏仁30g,赤小豆30g', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (78, '张磊补肝肾，祛风湿法治疗腰痛,胸痹', 1, '王某', '46', NULL, '男', '19980303', '持续性胸痛3年，腰痛20余年', '3年前出现胸痛，呈持续性发作，经服信他乐克，田尔欣，利脑欣等药治疗，症状减轻，但停药后又加重，伴双下肢木痛，近半年头懵头痛，小便黄，大便溏。', '胸痛，腰痛，肢痛，头痛，尿黄，便溏', '腰痛,胸痹', '心脏病,腰椎间盘脱出', '中医内科', '10剂,1日1剂 ,水煎服', '补肝肾，祛风湿法', '加味独活寄生汤', '独活10g,桑寄生15g,秦艽10g,防风10g,细辛3g,当归10g,川芎10g,白芍10g,熟地15g,桂枝10g,茯苓10g,炒杜仲10g,川断10g,狗脊30g,制川乌10g,炙甘草6g', '调情志，慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (79, '张磊燮理法治疗不寐', 1, ' 周某', '63', NULL, '男', '19980804', '夜寐差10余年', '十年前开始出现夜寐差，每晚能入睡4-5小时，伴有轻微头晕脑胀，经多次中西医治疗，罔效，大便2-3天一次，干结带血。 舌质： 舌质红。', '寐差，头晕，便干，便血', '不寐', '失眠,', '中医内科', '6剂,1日1剂 ,水煎服', '燮理法', '眠安汤', '百合30g,生地15g,麦冬30g,炒枣仁30g,茯神10g,胆星10g,灯心草3g,竹叶10g,生龙骨30g（先煎）,生牡蛎30g（先煎）,磁石30g（另包）,小麦30g,槐角30g,甘草6g,大枣6枚为引', '调情志', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (80, '张磊补中益气法治疗头痛', 1, '雷某', '60', NULL, '男', '19971226', '发作性左偏头痛10余年', '十年前不明原因出现左偏头痛，每因感冒或劳累饮酒后发作，使用抗生素治疗可使疼痛缓解，疼痛发作时左侧头皮肿胀，皮色不红，揉搓后加重，汗出干哕恶心，鼻塞流浊涕，遇冷疼痛加重，无口干苦，有心慌现象，蒙头休息后可暂时缓解，食欲、睡眠可，二便正常', '头痛，汗出，干哕，恶心，鼻塞，流涕，心慌', '头痛', '头痛', '中医内科', '6剂,1日1剂 ,水煎服', '补中益气法', '加味补中益气汤', '党参15g,黄芪30g,炒白术10g,当归10g,陈皮10g,升麻6g,柴胡6g,蔓荆子10g,川芎10g,炒白芥子10g,制川乌10g,炙甘草6g,生姜3片,大枣4枚（切开）为引 ', '劳逸结合', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (81, ' 张磊升阳健胃法治疗泄泻', 1, '李某', '45', NULL, '男', '19990708', '大便溏泻六年，加重一年。', '大便溏泻，挟有大量食物残渣，臭味不大，时轻时重，便前无腹胀，便后有虚脱感，汗出心慌，饥饿快，但不欲食，肠鸣，脘腹部怕冷，口腔溃疡，口苦干，头晕头昏，睡眠多梦，易醒，小便正常', '腹泻，饥不欲食，肠鸣，口疮，口苦，口干，头晕，多梦', '泄泻', '慢性腹泻', '中医内科', '6剂,1日1剂,水煎服', '升阳健胃法', '升阳益胃汤', '党参10g,黄芪15g,炒白术10g,黄连3g,半夏10g,陈皮10g,茯苓10g,泽泻10g,防风3g,羌活3g,独活3g,柴胡3g,白芍12g,荷叶10g,浮小麦30g,生龙骨30g（另包）,生牡蛎30g（另包）,炙甘草6g', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (82, ' 张磊清热化火，镇惊安神法治疗眩晕，不寐', 1, '魏某', '34', NULL, '女', '19990603', '头晕心慌失眠月余', '一个月前照顾病人，精神紧张，过度劳累，而现心慌、头晕、恶心，全身无力，失眠，每晚需服安定1-2片能休息5-6个小时。口苦，食欲不振，食后噫气，烦躁易怒，睡眠易惊醒，手足心热，躁汗时出，大便溏日1次，小便正常，已断经半年，白带适中', '心慌，头晕，恶心，乏力，失眠，口苦，纳差，嗳气，烦躁，易怒，手足心热，便溏', '眩晕,不寐', '失眠', '中医内科', '6剂,1日1剂 ,水煎服', '清热化火，镇惊安神法', '温胆汤', '半夏10g,陈皮10g,茯苓10g,炒枳实10g,竹茹30g,黄连6g,胆星10g,灯心草3g,生龙骨30g（另包）,生牡蛎30g（另包）,小麦30g,炒枣仁30g,谷精草30g,青葙子15g,荷叶30g,甘草6g', '调情志', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (83, '张磊养阴清热，化痰法治疗不寐', 1, '张某', '40', NULL, '女', '0', '失眠10余天', '患者经常有室性早搏2联、3联律。近10余天来失眠较重。每夜睡眠2-3小时，服安眠药效果欠佳。时有心烦，口腔溃疡，流泪，口干不苦，手心发热，心慌偶作，食欲可，二便正常', '失眠，心烦，口疮，流泪，口干，手心热，心慌', '不寐', '失眠', '中医内科', '6剂,1日1剂 ,水煎服', '养阴清热，化痰法', '自拟方', '百合30g,生地10g,麦冬15g,茯神10g,清半夏10g,胆星6g,小麦30g,炒枣仁30g,丹参30g,生龙骨30g（另包）,生牡蛎30g（另包）,黄连6g,甘草6g,大枣6枚为引', '调情志，慎风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (84, ' 张磊补肝通络化痰法治疗腰痛', 1, '何某', '47', NULL, '女', '19981211', '右侧腰至髂部酸沉20余天', ' 患者不明原因出现右侧腰至髂部酸沉，不痛不麻，休息后加重，活动后减轻。不影响正常生活，抬右腿时，腰部肌肉发紧，弯腰自如。饮食可，二便正常，月经正常，白带时多', '腰痛，白带多', '腰痛', '坐骨神经痛', '中医内科', '6剂,1日1剂 ,水煎服', '补肝通络化痰法', '曲直汤', '当归10g,丹参30g,山萸肉12g,知母10g,川牛膝10g,木瓜30g,橘络10g', '调情志，慎风寒，勿劳作', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (85, '张磊清胃热，利脾湿，理气法治疗嘈杂', 1, '王某', '36', NULL, '女', '0', '胃中嘈杂不适2天', '2天前无明显诱因出现间断性胃中嘈杂。口干时有口苦，无泛酸及疼痛，大便偏干，排解费力。小便正常，时觉喉咙有痰，小腹部隐痛。 舌质： 舌质瘀暗', '嘈杂，口干，口苦，便干，腹痛', '嘈杂', '胃炎,', '中医内科', '6剂,一日一剂,水煎服', '清胃热，利脾湿，理气法', '自拟方', '全瓜蒌15g,薤白10g,杏仁10g,桔梗10g,槐角30g,大云30g,炒枳实10g,厚朴10g,黄连6g,甘草3g', '节饮食，忌辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (86, ' 张磊祛湿和胃，理气消胀法治疗腹胀', 1, '郭某', '48', NULL, '男', '0', '胃脘嗔胀痞塞3个月', ' 患者平时饮酒较多。近3个月出现胃脘嗔胀痞塞，按之疼痛，食后加重，稍食即饱，偶有泛酸，无烧心，晨起口干苦。夜寐一般，二便正常', '腹胀，泛酸，口苦，口干', '腹胀', '浅表性胃炎,十二指肠溃疡', '中医内科', ' 6剂,1日1剂,水煎服', '祛湿和胃，理气消胀法', '半夏泻心汤', '半夏10g,茯苓10g,党参10g,干姜6g,黄芩10g,黄连6g,厚朴10g,香橼10g,醋元胡10g,蚤休15g,白芨10g,炒枳实10g,煅乌贼骨10g,炙甘草6g', '忌辛辣油腻，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (87, ' 张磊益气化湿，祛风通络法治疗痹证', 1, '杜某', '28', NULL, '女', '19990504', '双手、双膝周围、双足疼痛3月余', '3个月前在手腕近内关穴出现红肿疼痛，经服药物治疗无效，后发展至双膝、双足疼痛，时红肿，查血沉、抗“O”均正常，服汤药治疗有效，但不除根，疼痛夜间较重，痛时活动不自如，发胀。纳眠可，二便调', '手足痛，关节痛', '痹证', '风湿性关节炎', '中医内科', ' 6剂,1日1剂 ,水煎服', '益气化湿，祛风通络法', '黄芪赤风汤加减', '黄芪15g,赤芍15g,防风10g,木瓜30g,威灵仙15g,白芍15g,伸筋草30g,路路通10g,络石藤30g,海风藤30g,酒桑枝30g,连翘10g,甘草6g', '调情志，勿劳累，避风寒湿', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (88, '张磊降逆化痰，理气和胃法治疗呃逆', 1, '刘某', '27', NULL, '女', '19990305', '噫气频作吐白粘沫消瘦近2年', '患者一年半前开始出现噫气频作，发无定时，食后加重，夏较冬重。吐白粘沫，明显消瘦，1年多来体重下降30斤，未检查过，间断服吗叮啉片，服时有效，停药加重，食欲不振，夜寐差，二便调', '嗳气，吐白粘沫，消瘦，纳差，寐差', '呃逆', '胃神经官能症,', '中医内科', '9剂,一日一剂,水煎服', '降逆化痰，理气和胃法', '吴茱萸汤合旋复代赭汤', '旋复花10g（布包）,代赭石15g,半夏15g,党参10g,吴茱萸6g,炒枳壳10g,枇杷叶10g,佛手3g,香橼6g,甘草10g,生姜9g,大枣6枚', '调情志，勿劳累，避风寒，忌食生冷油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (89, ' 张磊清热利湿化痰，佐以祛风法治疗发热', 1, '宋某', '45', NULL, '男', '19970520', '低热4月余', '四个月前，由于感冒后出现低烧，体温波动在37.1摄氏度至37.8摄氏度之间，后又出现双手指关节肿大变形，有压痛，肤色不变，全身乏力，食少，经中西医结合治疗无效', '低热，关节肿，乏力，纳差', '发热', '不明原因低热', '中医内科', '10剂,一日一剂,水煎服', '清热利湿化痰，佐以祛风法', '自拟方', '半夏10g,陈皮10g,制南星10g,橘络10g,姜黄10g,酒桑枝30g,生薏苡仁30g,炒白芥子10g,竹叶10g,泽泻10g,威灵仙15g,防己10g,知母15g,鸡血藤20g,透骨草20g,甘草6g', ' 少食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (90, '张磊清热利湿法治疗湿疹', 1, '师某', '40', NULL, '男', '19980619', '全身出米粟样红疹2天。', '者发病前3天因饮酒过量，于昨日开始出现全身皮疹如米粟状，色红不痒，未服药治疗，平时易出虚汗，时腰隐痛不适，纳眠可，二便调。', '皮疹，汗出，腰痛', '湿疹', '荨麻疹', '中医内科', '1剂,1日1剂,水煎服', '清热利湿法', '自拟方', '葛根30g,猪苓10g,云苓10g,泽泻10g,连翘30g,白茅根30g,桑叶15g', '戒烟酒，避风寒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (91, '张磊涤浊法治疗哮喘', 1, ' 耿某', '32', NULL, '男', '20060410', '胸闷哮喘三年', '三年前不明原因胸闷气喘，时哮鸣，痰多色白而稠，夜间可平卧，寐安，二便正常，血压有时高。在县医院诊为：支气管炎。肺气肿，不多饮。口不干，声音嘶哑。', '胸闷,哮喘', '哮喘', '支气管炎', '中医内科', '10剂,1日1剂 ,水煎服', '涤浊法', '涤浊汤加减', '苇根30g,冬瓜仁30g,生薏苡仁30g,桃仁10g,制半夏10g,陈皮10g,茯苓10g,海浮石30g（包煎）,黄芩10g,炒白芥子6g,炒苏子6g,炒卜子6g,炙麻黄3g,生甘草6g', '调情志，勿劳累，忌辛辣刺激食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (92, '张磊化积导滞，和胃平肝法治疗胃痛', 1, '杨某', '57', NULL, '男', '19980106', '间断性胃脘疼痛6年，加重伴恶心呕吐7个月', '6年前不明原因出现间断性胃脘及两胁下疼痛，按胃溃疡治疗时轻时重。近7个月加重，痛时伴有恶心，且呕吐清水后可暂时缓解。饥饿时疼痛易发作，食后不能缓解，胃部怕凉，揉按后胃中舒适，呕吐时右偏头痛，口苦口干，胃痛反牵后腰背部，食欲可，二便正常，胃中无泛酸', '胃痛,恶心,呕吐,头痛,口苦,口干', '胃痛', '胃十二指溃疡', '中医内科', '4剂,1日1剂 ,水煎服。', '化积导滞，和胃平肝法', '自拟方', '半夏15g,吴茱萸6g,党参10g,槟榔10g,前胡10g,桔梗10g,鳖甲15g,干姜6g,元胡10g', '忌辛辣刺激性食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (93, '张磊清肝和胃，理气解郁法治疗郁证', 1, '韩某', '53', NULL, '女', '19971225', '进食后自感饮食停留食道中段不下半月', '半月前无明显诱因出现进食后饮食停留于食道中段不下。但是饮食已下，时欲吐酸，但吐不出，经服胃舒平疗效不佳。伴有口干苦，牙龈出血，食欲可，夜寐佳，二便正常，右胁偶有隐痛', '吞咽不利.吐酸,口干,口苦,牙龈出血,胁痛', '郁证', '神经官能证', '中医内科', '4剂,1日1剂 ,水煎服', '清肝和胃，理气解郁法', '自拟方', '炒枳实15g,苏梗10g,炒苏子10g,半夏12g,黄芩10g,代赭石15g,麦冬15g,川楝子6g,元胡10g,甘草6,陈皮10g', '忌食辛辣刺激食物,忌食生硬食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (94, '张磊益气养阴，润肺止咳法治疗咳嗽', 1, '王某', '54', NULL, '男', '19980612', '阵发性咳嗽，吐少量白粘痰20余天', '20天前因感冒致咳嗽，胸闷，经静滴鱼腥草3天，青霉素6天，又静滴先锋B三天，咳嗽较前减轻，痰量减少，胸闷减轻。6月8号查胸片示慢支合并肺气肿。食欲差，夜寐佳，二便调', '咳嗽,胸闷,寐差', '咳嗽', '慢性支气管炎,肺气肿,', '中医内科', '4剂,1日1剂 ,水煎服', '益气养阴，润肺止咳法', '自拟方', '北沙参30g,百合15g,黄芪10g,天冬10g,全瓜蒌10g,炒苏子10g,当归10g,苇根30g,炙桑皮10g,炙杷叶10g,金银花10g,橘仁6g,炒麦芽15g,生甘草6g', '慎风寒,生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (95, '张磊滋阴清热凉血法治疗斑疹', 1, '张某', '37', NULL, '男', '19981225', '双下肢红色斑疹20余天', '无明显诱因近20天来双下肢出点状红疹，高出皮肤，不痒不痛无渗出，时隐时现，时有红斑，按过敏治疗无效，渐加重，食欲可，夜寐佳，大小便正常', '斑疹', '斑疹', '过敏原因待查？', '中医内科', '6剂,1日1剂 ,水煎服', '滋阴清热凉血法', '自拟方', '生地20g,赤芍15g,丹皮12g,槐花30g,紫草12g,连翘12g,赤小豆30g,白茅根30g,忍冬藤30g,丝瓜络30g,甘草6g', '少食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (96, '张磊补气升陷法治疗郁证', 1, '马某', '54', NULL, '女', '19990409', '发作性胸胁向两侧扩张感.', '不明原因近1个月来出现阵发性胸胁向两侧扩张，汗出乏力，神志请，但不欲动，无胸闷胸慌胸痛，约25分钟后自行缓解，各项检查均无异常发现。纳眠可，大小便正常。一般发作多在白天', '胸胀', '郁证', '神经官能症,', '中医内科', '6剂,1日1剂,水煎服', '补气升陷法', '升陷汤', '党参15g,黄芪30g,知母15g,桔梗6g,升麻6g,柴胡6g', ' 畅情志。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (97, '张磊疏风清热泻火（轻清法）法治疗头痛', 1, '徐某', '30', NULL, '女', '19991203', '头痛时作时止15年。', '15年前出现头痛，多在头后部、前额、头顶两侧部位，疼痛多在一个部位，同时伴头木沉，经查脑电图，未发现异常，近段拍片示：额窦炎。疼痛多在受凉、劳累、生气、紧张时发作，食欲一般，夜寐一般，大便正常，小便黄，月经正常。', '头痛，尿黄', '头痛', '血管神经性头痛', '中医内科', '6剂,1日1剂 ,水煎服', '疏风清热泻火（轻清法）法', '谷精汤', '谷精草30g,青葙子15g,草决明10g,酒黄芩10g,薄荷10g（后下）,蔓荆子10g,牵牛子10g,元胡10g,虫蜕6g,川芎6g,甘草6g', '忌食辛辣食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (98, '张磊燮理法治疗泄泻', 1, '高某', '36', NULL, '女', '20060215', '腹泻1年余', ' 以前肠胃不好，1年前做“胆囊摘除术”（结石），后出现腹泻间作，多于受凉，食生冷后出现，泻前腹痛（左侧脐腹），里急后重，便溏，有时带泡沫，上午腹泻4-5次，下午较少，不能食生冷，小便夜频，眠差，有时入睡困难', '腹泻，腹痛，里急后重，便溏，夜尿频，寐差', '泄泻', '慢性结肠炎,', '中医内科', '6剂,1日1剂 ,水煎服', '燮理法', '山前汤（张磊教授经验方）加味', '炒山楂15g,生山楂15g,炒车前子15g（包煎）,生车前子15g（包煎）,陈皮10g,生白芍10g,防风3g,炒白术10g,炒山药15g,生山药15g,黄芩10g', '勿食生冷', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (99, ' 张磊疏风清热，益气活化固表法治疗丘疹,斑秃', 1, '马某', '58', NULL, '女', '20060111', '面部出红色丘疹7月余，斑秃5年', '原居上海，7月前面部出现红色丘疹，局部皮肤痒，服中西药效不明显。现症：面部出红色丘疹，局部皮肤痒，以眼角、口唇明显，可自行消退，外用“皮康王”后亦可缓解。头发脱落，斑秃。', '丘疹，脱发', '丘疹,斑秃', '皮炎,脱发', '中医内科', '7剂,1日1剂 ,水煎服', '疏风清热，益气活化固表法', '银翘散合黄芪赤风汤化裁', '金银花15g,连翘12g,竹叶10g,荆芥10g,薄荷10g（后下）,蝉蜕6g,牵牛子10g,防风10g,赤芍15g,生黄芪30g,生甘草6g', '忌辛辣刺激性食物。避紫外线', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (100, '张磊滋补肝肾，清肝止血法治疗眼珠痛,崩漏', 1, '赵某', '56', NULL, '女', '20060210', '眼珠痛间作4年。', '4年来经常下午眼珠痛，头痛，多于感冒或休息不好时眼珠胀痛，前额及太阳穴处痛，口不干，怕冷，夜间易醒，食欲可，月经淋漓已10天，量少，白带适中，大小便正常，胃胀痛，无烧心泛酸。', '眼痛，头痛，怕冷，月经量多，胃胀，胃痛', '眼珠痛,崩漏', '1，神经性眼珠痛？；2，宫血。', '中医内科', '6剂,1日1剂 ,水煎服', '滋补肝肾，清肝止血法', '自拟方', '熟地炭30g,女贞子15g,旱莲草30g,生白芍20g,川断10g,制首乌15g,夏枯草30g,陈皮6g,蔓荆子10g', '忌辛辣，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (101, '张磊化痰散结解毒法治疗瘰疠', 1, '贺某', '45', NULL, '男', '0', '左甲状腺癌部分切除术后1个月', '患者左侧甲状腺癌部分切除术1个月，无进行放、化疗治疗，口干夜间重，无口苦口渴，纳眠可，大小便正常，无心烦心慌，左颌下、左甲状腺仍有硬结节，如黄豆大小，触之坚硬不移，皮肤发紧。', '口干，甲状腺结节', '瘰疠', '复发性甲状腺癌', '中医内科', '16剂,1日1剂,水煎服', '化痰散结解毒法', '消瘰丸加味', '忍冬藤30g,炒白芥子10g,制南星10g,陈皮10g,夏枯草30g,连翘12g,赤芍30g,大贝10g,元参30g,皂刺12g,川芎10g,蚤休10g,通草6g,葛根30g,甘草10g', ' 勿食辛辣之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (102, '张磊养血散热法治疗眉棱骨痛', 1, '孙某', '33', NULL, '女', '20050923', '双眉棱骨疼痛1年', '眉棱骨痛以攒竹穴处为重，时轻时重夜间易发作，伴有恶习呕吐，胸闷噫气，睡眠休息后明显好转，可以自行缓解，眼不胀，食欲可，口不干苦，大小便正常，月经正常，白带适中，平时易上火。', '眉棱骨痛，恶心，呕吐，胸闷，嗳气', '眉棱骨痛', '神经性头痛,慢性肾病', '中医内科', '15剂,1日1剂,水煎服', '养血散热法', '加味四物汤', '熟地10g,当归10g,白芍15g,川芎6g,菊花10g（后下）,夏枯草30g,白芷10g,羌活10g,虫蜕6g,连翘10g,元参30g,知母15g', '勿食辛辣 ，注意休息', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (103, '张磊化痰清热法治疗不寐', 1, '聂某', '36', NULL, '男', '19980317', '不寐1年余', '1年前不明原因出现不寐，渐加重，发展至彻夜不眠，服西药治疗效果不佳，每晚服舒乐安定片5-6片，能入睡4-5小时，伴头晕乏力，心烦急躁，食欲尚可，大小便正常。 辨证论治：痰火内扰，心神不宁。', '失眠，头晕，乏力，心烦，急躁', '不寐', '失眠', '中医内科', '15剂,1日1剂 ,水煎服', '化痰清热法', '黄连温胆汤,栀子豉汤', '半夏12g,云苓12g,陈皮10g,炒枳实10g,竹茹20g,黄连10g,胆南星10g,天竺黄10g,生龙骨30g,生牡蛎30g,小麦30g,黑栀子6g,淡豆豉20g,甘草6g', ' 忌辛辣，生活规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (104, ' 张磊益气化瘀，通络清热法治疗痹证', 1, '杜某', '58', NULL, '女', '19990504', '双手足及双膝关节疼痛3月余。', '3个月前在手腕外内关穴处红肿疼痛，经治疗无效（用药不详）后发展至双手双足及双膝关节疼痛，痛甚则红肿，查血沉，抗“O”类风湿因子均正常，以夜间较重，局部有微热感，大小便正常，月经数月一来', '关节痛，月经后期', '痹证', '风湿性关节炎？', '中医内科', '6剂,1日1剂 ,水煎服', '益气化瘀，通络清热法', '黄芪赤风汤', '黄芪15g,赤芍15g,防风10g,木瓜30g,威灵仙15g,白芍15g,伸筋草30g,路路通10g,络石藤30g,海风藤30g,酒桑枝30g,连翘10g,甘草6g', '避风寒，防感冒，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (105, ' 张磊清热凉血法治疗红汗', 1, '刘某', '41', NULL, '女', '20050406', '红汗半年', '不明原因出现洗手洗足水呈粉红色，穿白色内衣可染成粉红色，数日不洗脚，血色凝于皮肤，近半年出汗不多，双膝以下浮肿，时头晕，纳眠可，口干口苦，大小便正常', '红汗，肢肿，头晕，口干，口苦', '红汗', '植物神经功能紊乱。', '中医内科', '10剂,1日1剂 ,水煎服', '清热凉血法', '自拟方', '连翘12g,金银花15g,生地炭30g,紫草10g,丹皮10g,白茅根30g,槐花30g,桑白皮10g,地骨皮10g,赤小豆30g,甘草6g,炒麦芽20g,陈皮10g,', '勿食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (106, '张磊益气养阴，清热利湿法治疗乳中结核,汗证', 1, ' 贾某', '55', NULL, '女', '20050314', '盗汗半年余', '盗汗半年余，时阵发性咳嗽，痰少色白，耳鸣，食欲一般，口干，心烦急躁', '盗汗，咳嗽，耳鸣，口干，心烦，急躁', '乳中结核,汗证', '植物神经功能紊乱,乳腺增生', '中医内科', '6剂,1日1剂 ,水煎服', '益气养阴，清热利湿法', '当归六黄汤', '生地10g,熟地10g,黄芩10g,黄连6g,黄柏10g,当归10g,生黄芪30g,浮小麦30g,煅牡蛎30g', '忌食辛辣刺激食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (107, '张磊益气养阴法治疗汗证', 1, '徐某', '43', NULL, '男', '0', '自觉发热汗出1年', '1年前因感冒服西药发汗致大汗，此后经常恶寒发热。但体温不高，夜间较白天重。多于醒后汗出，乏力，时有心慌，左胸背部窜痛时作时止，食欲一般，夜寐好，大便溏日2次，多项检查未发现异常。', '发热，自汗，乏力，心慌，胸痛，便溏', '汗证', '植物神经功能紊乱', '中医内科', '3剂,1日1剂 ,水煎服', '益气养阴法', '当归六黄汤,', '当归10g,熟地10g,生地10g,黄芩10g,黄连6g,黄柏6g,生黄芪30g,炒枣仁15g,山萸肉15g,浮小麦30g,炒白术15g,制附子10g', '避风寒，防感冒，勿劳累。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (108, '张磊补气升提，理气涤浊法治疗腹胀', 1, '鲁某', '42', NULL, '女', '19981008', '经常腹部憋胀下坠7年', '7年来经常腹部憋胀下坠，时有微痛，大便2-3天1次，微溏，质粘。频欲大便，但不能解下。经B超检查：盆腔积液，子宫下垂。服西药甲硝唑，PPA药物暂时有效，停药即发。大便后胀痛减轻，劳累后加重。曾服中药升提药觉不适，服理气药腹胀更甚，服泻药即时舒服。月经提前5-6天，量稍多，食欲可，夜寐多梦。', '腹胀，便溏，月经先期，寐差', '腹胀', '盆腔炎女性,1，子宫脱垂，2，盆腔炎', '中医内科', '12剂,1日1剂,水煎服', '补气升提，理气涤浊法', '补中益气汤', '党参10g,黄芪15g,炒白术10g,当归10g,陈皮10g,升麻3g,柴胡3g,厚朴12g,大黄6g,炒枳实10g,生意苡仁30g,冬瓜仁30g,败酱草30g,黄芩10g,泽泻10g,桃仁10g,甘草6g,', '调情志，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (109, '张磊滋阴润燥法治疗便秘,不寐', 1, '张某', '54', NULL, '女', '19981211', '大便秘结2年，不寐1年余。', '患者大便秘结2年，4-7天大便1次，服通便灵，果导片才能大便，近1年又增不寐多梦，食欲不振，月经后错7-14天，量少，心烦急躁。', '便秘，失眠，寐差，纳差，心烦，急躁', '便秘,不寐', '结肠炎,失眠,', '中医内科', '6剂,1日1剂 ,水煎服', '滋阴润燥法', '麻子仁丸', '炒火麻仁30g,炒枳实10g,大黄3g,厚朴10g,槐角30g,草决明30g,当归10g,炒枣仁30g,灯心草3g,桃仁10g', '忌食辛辣食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (110, ' 张磊养阴润肺，止咳平喘法治疗哮喘', 1, '刘某', '53', NULL, '女', '19990427', '发作性哮喘2年，再发20天', '患者近2年经常出现发作性哮喘，干咳无痰，动则加重。多于闻刺激性气味诱发，夏季轻。口服咳喘灵，氨茶碱治疗有效，但不能消失。呼吸困难，闷气，喉中痰鸣，食欲一般，大便干结。口干口苦', '哮喘，呼吸困难，胸闷，痰鸣，便干，口干，口苦', '哮喘', '过敏性哮喘,', '中医内科', ' 6剂,1日1剂 ,水煎服', '养阴润肺，止咳平喘法', '定喘汤', '南沙参15g,北沙参15g,天冬10g,炙麻黄10g,射干10g,炒苏子10g,当归6g,白果10g,黄芩10g,炙紫苑15g,五味子10g,白前15g,桔梗15g,杏仁10g,甘草6g', '忌食辛辣刺激食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (111, '张磊灵动法治疗胃痛', 1, '王某', '42', NULL, '女', '0', '后半夜胃痛1年余。', '每晚2点多胃痛，嘈杂不适，按揉觉舒，伴烧心，无泛酸，无噫气，不能食生冷，大便有时干，日1次，小便夜频，眠差，纳差，不能食硬食，夜晚口干。近10余日“感冒”，现：咳嗽，吐粘痰，咽痒。', '胃痛，嘈杂，烧心，便干，夜尿频，寐差，纳差，口干，咳嗽，咽痒', '胃痛', '慢性红斑性胃炎', '中医内科', '7剂,1日1剂,水煎服', '灵动法', '健脾丸', '党参10g,炒白术6g,茯苓10g,制半夏10g,陈皮6g,砂仁3g（后下）,草果3g,草蔻3g,黄连3g,蚤休10g,元胡6g,煅瓦楞子15g,决明子20g,杏仁10g,生甘草6g', '忌肥甘厚味', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (112, '张磊涤浊降气，平喘法治疗哮喘，咳嗽', 1, '刑某', '50', NULL, '男', '20051202', '胸闷气喘心慌5年', '有慢性咳嗽病史10余年。近5年胸闷气喘心慌，动则甚左前胸及后背痛，喉中痰鸣，耳鸣，口干不苦，咳嗽吐白痰，食欲可，大便溏日2次，小便有解不尽感', '胸闷，气喘，心慌，痰鸣，耳鸣，口干，咳嗽，便溏，尿不尽', '哮喘,咳嗽', '支气管哮喘', '中医内科', '12剂,1日1剂 ,水煎服', '涤浊降气，平喘法', '自拟方', '桂枝10g,白芍10g,厚朴12g,杏仁10g,冬瓜仁30g,生薏仁30g,茯苓12g,黄芩10g,海浮石30g（先煎）,白前10g,橘红10g,炒枳壳10g,生甘草6g, 生姜3片,大枣4个（切开）为引', '调情志，勿劳累，避风寒，忌辛辣刺激食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (113, '张磊补肾养血固冲法治疗漏下', 1, '王某', '50', NULL, '女', '20050425', '月经淋漓不断3月余，腰痛、腿酸2个月', '05年3月份“剖腹产”“大出血”，3个月前出现月经淋漓不断，色褐，经期前期，或后期，服调经药后现月经提前1周，色鲜红，量可，经前小腹胀，腰痛，经前加重，与天气变化无关，腿酸，膝部明显，身畏寒，活动后易汗出，小便夜频，大便干，1日1次，纳眠可，口干，鼻塞，咽干，近1周“感冒”。', '月经过多,汗出,夜尿频,便干,口干,鼻塞,咽干', '漏下', '宫血', '中医内科', '20剂,1日1剂 ,水煎服', '补肾养血固冲法', '自拟方', '熟地炭30g,制首乌15g,荆芥炭10g,茜草炭10g,乌贼骨30g,川断10g,女贞子15g,旱莲草30g,麦冬10g,山萸肉10g', '调情志，勿劳累，忌食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (114, '张磊治宜燮理法治疗泄泻', 1, '宋某', '59', NULL, '男', '0', '腹泻、心慌、头晕、身乏力4月余', ' 曾住院治疗三次乏效，诊断为：酒精性肝病，酒精性心肌病，酒精性神经病病变并肌萎缩，小肠吸收不良综合征并中毒营养不良。现症：腹泻，1日5、6次甚10余日次，水样便带不消化食物，肠鸣便后小腹痛，小腹畏寒，得暖觉舒，心慌，头晕，身乏力，小便可，纳少，食后即泻，身体消瘦，手凉。', '腹泻、心慌、头晕、乏力、消瘦、手凉', '泄泻', '酒精性肝病，酒精性心肌病，酒精性神经病病变并肌萎缩，小肠吸收不良综合征并中毒营养不良。', '中医内科', ' 4剂,1日1剂 ,水煎服', '治宜燮理法', '山前汤（张磊教授经验方）化裁', '炒山楂15g,生山楂15g,炒车前子15g（包煎）,生车前子15g（包煎）,赤石脂30g,干姜10g,炒山药15g,生山药15g,禹余粮30g', '忌食肥甘厚味', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (115, '张磊温补脾肾，涩肠止泻法治疗泄泻', 1, '王某', '55', NULL, '女', '0', '五更泻10余年', '患者从小肠胃弱，30多岁时曾经腹泻一段时间，体重下降，消瘦。吃青菜、油腻则泄泻，尤其五更泻时间长，腹不胀，纳少，乏力腿软，下肢发凉，口干欲饮，每次能饮水4-5市斤', '五更泻，消瘦，纳差，乏力，口干，多饮', '泄泻', '慢性结肠炎。', '中医内科', ' 6剂,1日1剂 ,水煎服', '温补脾肾，涩肠止泻法', '四神丸合附子理中汤加减', '补骨脂10g,吴茱萸6g,五味子10g,肉桂6g,制附子10g（先煎）,煨肉豆蔻10g,炒山药30g,乌梅6g', '忌辛辣刺激性食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (116, ' 张磊凉血清热，祛湿法治疗牛皮癣', 1, '许某', '26', NULL, '男', '20050801', '全身多发性点状牛皮癣1年余', '患者全身多发性点状牛皮癣1年余，经我处治疗已愈。现症：腰酸背痛，大便溏3-4次/天，头懵，目湿困，右耳鸣，全身困乏，口干饮水可，小便可，纳可', '牛皮癣，腰酸，背痛，便溏，头懵，耳鸣，乏力，口干', '牛皮癣', '红皮病性银屑病(银屑病性红皮病)', '中医内科', '10剂,1日1剂 ,水煎服', '凉血清热，祛湿法', '自拟方', '炒苍术20g,炒薏仁30g,土茯苓30g,槐花30g,连翘15g,赤小豆30g,白鲜皮30g,制首乌15g,当归10g,赤芍30g,泽泻10g,生甘草6g', ' 少食辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (118, '张磊益气温胃，温中补虚法治疗胃脘痛', 1, '周某', '65', NULL, '女', '19761103', '噫气频频,艰于饮食', '去年发现子宫癌,经放疗，效果较好，为了巩固疗效，近又用化疗,继而发生胃脘痛,噫气频频,艰于饮食。在本单位医院胃肠钡餐透视,发现胃窦大弯局部粘膜中断,充盈不良,胃移位至髂骨嵴下10cm.诊为胃溃疡、胃癌。曾服西药无效，如此1个月多，身体逐渐瘦弱', '噫气，胃痛', '胃脘痛', '胃痛', '中医内科', '2剂,水煎服', '益气温胃，温中补虚法', '丁香柿蒂汤，吴茱萸汤加减', '半夏9g,丁香6g,柿蒂15g,党参15g,降香9g,炒枳壳9g,吴茱萸9g,高良姜9g', NULL, '上药服完2剂,胃痛、噫气均止。照上方继服2剂。其，科沃木11月8日三诊:上病未再发作，唯觉胃部不适,纳食尚少,但无大碍。', '熟地黄18g,当归9g,制附子9g,干姜6g,炙甘草9g。水煎服。', NULL, NULL, NULL, NULL, '此为中焦虚寒，胃气上逆之候,故用丁香柿蒂汤以益气温胃降逆,吴茱萸汤以温中补虚降逆。高良姜温胃散寒止痛作用较好，故以高良姜易方中生姜，加降香、枳壳以助其降逆之力，且降香又能行瘀定痛，以解血气因寒而凝涩作痛之苦。二方虽略加减,但仍不悖二方之用。', NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (119, '李振华滋阴潜阳、平肝熄风法治疗头痛', 2, '杨某', '35', NULL, '男', '19920619', '头痛4年，手颤4月', '5年前退休以后，患者情绪不畅，常头痛头晕，视物不清，经河南省人民医院检查，诊断为“白内障”，1年后行白内障手术治疗。术后配戴眼镜，不久又出现头痛头晕，初疑为眼镜矫正度数不够所致，遂到医院检查，初步诊断为：脑动脉硬化，遂即服药予以治疗（具体治疗药物不详）。4个月前，无明显诱因，患者头痛、头晕加重，合并双手不自主振颤，反映迟钝。河南省人民医院颅部CT报告示：脑萎缩。特请李老予以诊治。现症：头痛，头晕，双手振颤，反应迟钝，口燥咽干，身困乏力，腰膝酸软，夜汗，睡眠差。 ', '头痛，头晕，手颤，反应迟钝，口燥，咽干，身困，乏力，腰酸，盗汗，寐差', '头痛', '脑动脉硬化,脑萎缩,无', '中医内科', '15剂,内服,水煎', '滋阴潜阳、平肝熄风法', '天麻钩藤饮,六味地黄汤', '方1,蒸首乌20g,白芍15g,杞果15g,山芋肉15g,黄精15g,珍珠母15g丹皮10,g,天麻10g,菊花12g,郁金10g,菖蒲10g,丹参15g,桑枝30g,地龙15g,钩藤12g,甘草3g。 方2,蒸首乌2g,白芍15g,杞果15g,山芋肉15g,山药20g,云苓15g,黄精15g,丹参15g,鸡血藤30g,地龙15g,乌鞘蛇12g,桑枝30g,天麻10g,菊花12g,细辛5g,丹皮10g,珍珠母15g,天麻10g,地龙15g生地15g,甘草3g。', ' 注意休息，调理情志。清淡饮食，忌辛辣油腻之物。上二方交替水煎服，每日一剂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (120, '李振华温中健脾、理气止痛法治疗胃痛', 2, '符某', '35', NULL, '女', '19910612', '胃脘部疼痛十年近日加重', '患者往常胃痛吐酸水，吃多了胃脘痞塞满闷不舒，吃少了不到时间就饿，胃中难受，为此曾多次到医院检查，经钡餐透视，诊为胃下垂及十二指肠球部溃疡。虽经多方治疗，病情时好时坏，一直没得痊愈。遇心情不好或受冷之时，每每复发，故来求治。近日因食凉饭，致胃脘疼痛复发，症见胃脘发胀疼痛，吐清水、食欲下降、饭量减少、神疲乏力、小便清、大便干、头晕、夜眠多梦、四肢欠温、有时嗳气', '胃胀、胃痛、纳差、神疲、乏力、便干、头晕、多梦、肢凉、嗳气', '胃痛', '十二指肠溃疡,胃下垂,慢性胃炎,', '中医内科', '5剂,每日一剂，分两次口服,水煎服', '温中健脾、理气止痛法', '愈疡健中汤', '太子参15g,白术10g,云苓15g,桂枝6g,白芍15g,砂仁8g,川朴10g,甘松10g,刘寄奴12g,元胡10g,乌贼骨10g,乌药10g,甘草3g', '忌食生冷和刺激性食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (123, '李振华健脾化痰,宣肺止咳法治疗咳嗽', 2, '张某', '57', NULL, '男', '0', '咳嗽、痰多一周', '自述一周前因饮食不慎，过食生冷，加之受寒，致咳嗽阵作，痰多，色白质稀，胸脘痞闷，食少纳呆，身倦乏力，经用止咳化痰及抗生素等药物治疗效果不佳', '咳嗽、多痰、胸闷、纳差、乏力', '咳嗽', '急性支气管炎', '中医内科', '6剂,每日一剂,水煎', '健脾化痰,宣肺止咳法', '健脾止咳汤', '党参10g,白术10g,茯苓15g,橘红10g,半夏10g,桔梗5g,木香6g,砂仁8g,厚朴10g,杏仁10g,苏子10g,桔梗10g,枳壳10g,甘草3g', '避风寒，戒烟，忌食油腻、甜食及辛辣食品。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (124, '李振华健脾燥湿，温化痰饮法治疗胃脘痛', 2, '曾某', '54', NULL, '男', '0', '胃脘痞胀，动则水响有声月余', '一年前曾因胃脘痞胀，经某医学院诊为“胃潴留”经用中西药治疗，病好转，但未痊愈。近因饮食不当，旧病复发，胃脘痞痛，喜暖喜按，间吐清酸，吐后稍快，动则胃内水响有声，上下冲击。食少纳呆，神疲乏力，心悸气短，头晕目眩，甚则颠仆不省，形寒肢冷，四肢欠温，大便时结时溏，小便短少', '胃胀、吐酸、纳呆、神疲、乏力、心悸、气短、头晕、目眩、肢凉、大便不调', '胃脘痛', '胃潴留', '中医内科', '3剂,每日一剂，分两次口服,水煎服', '健脾燥湿，温化痰饮法', '加减胃苓汤', '苍术30g,云苓15g,枳实10g,川朴10g,陈皮10g,半夏10g,干姜6g,肉桂6g,白术10g,山药20g,甘草3g', '忌食生冷油腻之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (125, '李振华健脾疏肝、理气化痰法治疗梅核气', 2, '李某', '43', NULL, '男', '0', '咽中不舒，如物梗阻1月余', '患者一月前因情志不遂，出现咽中似有异物梗阻，吐之不出，咽之不下，服冬凌草片，山豆根片，草珊瑚含片等，症状无明显变化，每因情志不遂，饮食不当症状加重。经当地医院五官科检查诊断为慢性咽炎。现咽中似有异物梗阻，口干不欲饮，胸胀满，腹胀纳差，身倦乏力。', '咽梗，口干，胸满，腹胀，纳差，乏力', '梅核气', '慢性咽炎', '中医内科', '12剂,每日一剂,水煎', '健脾疏肝、理气化痰法', '理气消梅汤（经验方）', '白术10g,茯苓15g,桔红10g,半夏10g,香附10g,厚朴10g,紫苏6g,枳壳10g,郁金10g,砂仁8g,牛旁子10g,桔梗10g,山豆根10g,射干10g,甘草3g', ' 畅情志，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (126, '李振华益气健脾，宣肺解表法治疗感冒', 2, '谭某', '57', NULL, '男', '19920310', '头痛，咳嗽反复发作月余', '患者自述春节以前，因劳累，一日晚饭后出现恶寒发热，其热不扬，但觉时时形寒，自汗出。头痛鼻塞，咳吐白痰，浑身倦怠无力，动则气短，连话亦不想多说。曾经卫生所用药，时好时坏，一直没能彻底治愈。往往劳累以后很容易发作。昨日参加单位义务劳动，晚上又出现身上发冷，体温38.2℃，汗多，头痛，鼻塞，咳嗽，吐白色稀痰，体倦乏力，动则气短，食欲欠佳，大小便正常', '恶寒，发热，汗多，头痛，鼻塞，咳嗽，乏力，气短，纳差', '感冒', '感冒', '中医内科', ' 3剂,口服,水煎', '益气健脾，宣肺解表法', '加味六君子汤', '党参15g,白术10g,云苓15g,橘红10g,半夏10g,苏叶10g,葛根10g,前胡10g,桔梗10g,杏仁10g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g,生姜三片为引。', ' 1、加强体育锻炼。 2、注意饮食，忌辛辣油腻。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (127, ' 李振华温中健脾，运化寒湿法治疗久痢', 2, '刘某', '56', NULL, '男', '19911015', '大便不爽，里急后重八、九日', '患者于半月前因腹痛绵绵，痢下清稀，里急后重，在当地医院诊为菌痢。用西药治疗得以好转，未能根治，时有复发。此次已下痢八、九日，现症见大便有粘液、排便不爽，里急后重，日行4-5次，左下腹绵绵作痛，体重减轻，食欲减少，大便稀薄，四肢欠温', '腹痛，里急后重，便粘，排便不爽，消瘦，纳差，肢凉', '久痢', '慢性结肠炎,', '中医内科', ' 5剂,口服,水煎', '温中健脾，运化寒湿法', '四君子汤合理中汤', '党参15g,白术10g,云苓15g,干姜10g,元桂6g,陈皮10g,苡仁20g,泽泻10g,白芍10g,甘草3g,苍术10g。', '注意饮食，忌生冷油腻，宜食清淡易消化之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (128, '李振华温补脾肾，益气升阳法治疗泄泻', 2, ' 刘某', '44', NULL, '女', '20050311', '大便时溏时泻15年余', '患者自述于15年前因经常饮食不节致大便时溏时泻，虽长期服用多种抗生素（氟哌酸、黄连素等）治疗，但病情时轻时重，反复发作，且每因饮食不调或劳累使病症加重。1995年曾服中药及中药灌肠（具体药物不详），但终未痊愈。2004年10月因饮食生冷致病情加重，经省人民医院纤维结肠镜检查提示：肠粘膜充血水肿明显，有散在糜烂，诊断为“慢性结肠炎”。来诊时症见黎明前腹痛肠鸣，大便溏薄，甚或完谷不化，日3－5次。食少腹胀，肛门下坠，畏寒肢冷，身倦乏力', '腹痛，肠鸣，便溏，纳差，腹胀，肛坠，畏寒，肢冷，身倦，乏力', '泄泻', '慢性结肠炎', '中医内科', '12剂,每日一剂,水煎', '温补脾肾，益气升阳', '四神丸合补中益气汤', '肉豆蔻10g,呉茱萸5g,补骨脂12g,党参12g,白术10g,茯苓20g,炒白芍10g,生黄芪15g,柴胡6g,升麻6g,薏苡仁30g,可子肉12g,砂仁8g,陈皮10g,泽泻10g,煨姜5g,制附子10g,炙甘草6g,生姜3片', '忌生冷、油腻及不易消化食物，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (129, ' 李振华清肺泄热，祛痰排脓法治疗肺痈', 2, ' 杨某', '64', NULL, '女', '19920304', '咳嗽、吐痰，痰中带血半年余。', ' 患者自述数年前曾经得过肺痨，经用链霉素和雷米封等药治疗，已经痊愈。半年前因感冒发烧引起咳嗽，经用中西药物治疗，感冒等症逐渐消失，唯咳嗽吐痰长期不愈。X光透视显示肺部纹理增粗。现症见咳嗽吐痰，尤以早晚为重，痰呈黄绿色，有时痰中带脓血，咽干口渴，有时发冷发热，头痛身困，小便色黄，食欲减退，不知饥，不欲食', '咳嗽，咯血，咽干，口渴，头痛，身困，尿黄，纳差', '肺痈', '支气管扩张', '中医内科', '10剂,每日一剂，早晚温服,水煎', '清肺泄热，祛痰排脓法', '加味苇茎汤,', '苇茎30g,生苡仁30g,桃仁9g,冬瓜仁18g,桔梗9g,鱼腥草30g,黄芩9g,连翘12g,金银花15g,川贝母9g,白芨9g,甘草3g,白茅根30g,黑地榆12g。', '1、坚持服药（中药）治疗； 2、忌烟酒及辛辣食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (130, '李振华健脾化湿，理肺祛痰法治疗咳嗽', 2, '张某', '38', NULL, '男', '0', '咳嗽吐痰，胸脘痞闷两个月。', '患者于四月中旬感冒以后，咳嗽一直未愈。曾服中成药半夏露、复方枇杷露和桑菊感冒片、止嗽散等药，效果一直不理想。现今咳嗽痰多，色白而粘，胸脘痞闷，食后腹胀恶心，食欲减退，大便不实', '咳嗽，多痰，胸闷，腹胀，恶心，纳差，便稀', '咳嗽', '慢性气管炎', '中医内科', '5剂,每日一剂，早晚温服,水煎', '健脾化湿，理肺祛痰法', '加味二陈汤', '苍术10g,白术10g,云苓15g,陈皮10g,半夏10g,川朴10g,旋覆花10g,桔梗10g,枳壳10g,炙甘草6g,炙紫菀10g,炙冬花10g,生姜三片,大枣五枚为引', '注意饮食，忌食辛辣油腻之物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (131, '李振华宣肺散寒、祛痰平喘法治疗哮证', 2, ' 梁某', '54', NULL, '男', '19830305', '咳嗽、气喘、喉中痰鸣一月余', '自述一月前因受凉而感冒咳嗽，治疗一周，咳喘不减，且逐渐加重，入某院治疗，胸部摄片检查提示支气管炎合并感染，用青霉素、链霉素、氨茶碱等西药1周，病情有所控制而出院，出院后第3天病情再次加重，胸闷气短，喘息不得卧，喉间痰鸣。复用抗生素、止咳平喘药治疗效果不佳。现喘促胸闷，不能平卧，形寒怕冷，咳嗽气逆，吐稀白涎沫，量多，喉间痰鸣, 腹胀纳差，大便溏薄', '咳嗽，气喘，胸闷，形寒，肢冷，痰鸣，腹胀，纳差，便溏', '哮证', '喘息型支气管炎', '中医内科', '3剂,每日一剂,水煎', '宣肺散寒、祛痰平喘法', '加减小青龙汤合二陈汤', '麻黄5g,杏仁10g,干姜5g,细辛5g,五味子10g,苏子10g,前胡10g,桔梗10g,炙桑皮12g,炙冬花10g,陈皮10g,半夏10g,茯苓10g,甘草3g。', '避风寒，戒烟，忌食油腻、甜食及辛辣食品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (132, '李振华健脾益气、祛湿化痰法治疗胃脘痛', 2, '杨某', '65', NULL, '女', '0', '胃脘疼痛时常发作19年', '患者自述胃脘疼痛时常发作已19年。1994年8月经河南省人民医院电子胃镜确诊为慢性浅表性胃炎，常服西药，中药汤剂等可取缓解之效。以后饮食稍有不慎或受凉即发胃痛。现症见胃脘隐痛不休，喜暖喜按，腹胀纳差，痞满嗳气，身倦乏力，四肢欠温，大便溏薄', '胃痛，腹胀，纳差，嗳气，乏力，肢凉，便溏', '胃脘痛', '浅表性胃炎,', '中医内科', ' 26剂,每日一剂,水煎', '健脾益气、祛湿化痰法', '加减香砂六君子汤', '党参15g,炒白术15g,茯苓18g,桔红10g,半夏10g,木香6g,厚朴10g,枳壳10g,柿蒂15g,薏苡仁30g,泽泻12g,桂枝3g,干姜3g', '饮食要有规律，保持心情舒畅。忌食油腻、甜食及辛辣食品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (133, ' 李振华温中健脾，疏肝和胃法治疗痞证', 2, '王某', '36', NULL, '男', '19870403', '反复胃满腹胀十年余', '患者于10年前因情志不畅出现胃满腹胀。以后常因饮食失宜或情志不畅症状加重。1986年4月经纤维胃镜检查及病理活检：胃粘膜萎缩性胃炎伴轻度肠上皮化生。诊为萎缩性胃炎。近年来经常出现胃满腹胀，时轻时重，喜温喜按，饮食减少，食后胀满，下午及夜间尤甚，大便溏，日行1-2次，四肢倦怠乏力。', '胃胀，腹胀，便溏，乏力', '痞证', '萎缩性胃炎', '中医内科', '25剂,每日一剂，早晚温服,水煎', '温中健脾，疏肝和胃法', '香砂温中汤', '党参12g,白术10g,茯苓10g,陈皮10g,半夏10g,香附10g,砂仁8g,厚朴10g,乌药19g,丁香5g,干姜10g,山楂15g,神曲12g,麦芽12g,甘草3g', '注意饮食，忌食辛辣油腻之物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (134, '李振华养阴柔肝法治疗胁痛', 2, '杨某', '43', NULL, '女', '19920322', '右侧胁肋隐痛，脘腹痞塞满闷年余', '患者自述素有胃痛，胃脘常常隐痛不舒，得暖得食则减，精神疲乏，头晕耳鸣，不断服用一些西药，病情时轻时重。从前年始，右侧胁肋隐隐作痛，悠悠不休，口干咽燥，心中烦躁，脘腹痞塞满闷不舒，大便干，食欲减退，神困乏力，失眠多梦', '胃痛，胁痛，腹胀，口干，咽燥，心烦，便干，纳差，乏力，寐差，多梦', '胁痛', '肋间神经痛', '中医内科', '3剂,每日一剂，早晚温服。,水煎', '养阴柔肝法', '一贯煎', '生地18g,枸杞15g,沙参15g,麦冬15g,当归10g,杭白芍15g,川楝子10g,丹参20g,山萸肉10g,枣仁15g,节菖蒲10g,元胡10g,甘草3g', '注意保持情志舒畅；注意饮食，忌辛辣油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (135, '李振华养阴舒肝，理气健脾法治疗胁痛', 2, '平某', '51', NULL, '女', '0', '胁痛年余', '患者自述自1990年下半年来，两侧胁肋隐痛，其痛悠悠不止，同时伴有饮食欠佳，口干咽燥，面部烘热，心中烦热，两颧发红，视物昏花，虽经当地中西药物治疗，见效甚微，无奈来郑治疗。现症见两胁时常疼痛，绵绵不休，饮食欠佳，饭菜不香，一日仅食5-6两，且食后腹胀，经常口干舌燥，心中烦热，头晕耳鸣，视物昏花', '胁痛，腹胀，纳差，口干，舌燥，心烦，头晕，耳鸣，目昏', '胁痛', '慢性肝炎,肋间神经痛,', '中医内科', '6剂,每日一剂，早晚温服,水煎', '养阴舒肝，理气健脾法', '一贯煎,逍遥散', '生地15g,杞果10g,沙参10g,麦冬10g,当归10g,柴胡6g,白芍15g,云苓15g,生白术10g,薄荷10g,白蒺藜10g,女贞子10g,甘草3g', '注意情志，心胸宜开阔；注意饮食，忌辛辣油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (136, '李振华健脾和胃、疏肝活瘀法治疗胃脘痛', 2, '王某', '59', NULL, '男', '19930319', '间断性胃脘疼痛十余年。', '患者自述间断性胃脘疼痛已十余年，疼痛时自服西药以求缓解。近一年来疼痛次数愈益频繁，程度日益加重。1998年12月3日经河南中医学院第一附属医院胃镜及胃粘膜活检确诊为浅表－萎缩性胃炎。现胃脘隐痛，间有刺痛，终日不休，痛及两胁，腹胀、嘈杂、食后胀甚、嗳气、少食，日进食量约250g左右，大便溏薄，身倦乏力。', '胃痛，胁痛，腹胀，嘈杂，嗳气，纳差，便溏，乏力', '胃脘痛', '慢性浅表－萎缩性胃炎。', '中医内科', ' 10剂,每日一剂,水煎 ', '健脾和胃、疏肝活瘀法', '加味香砂六君子汤', '党参15g,白术12g,茯苓18g,陈皮10g,半夏10g,木香10g,香附10g,郁金12g,枳壳10g,元胡10g,川芎10g,砂仁6g,炙甘草5g。', '忌生冷、油腻及不易消化食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (139, ' 李振华补肾填精、祛风止痛法治疗头痛', 2, '王某', '47', NULL, '女', '19920318', '头痛伴耳呜、耳聋半年', ' 5个月前感冒后，未给予正规治疗，头痛一直绵延至今，时轻时重，遇劳则甚。虽多方医治，效果不佳，后又出现记忆力减退，耳鸣、耳聋，腰膝酸软，身困乏力。现头痛时作，痛连两侧太阳穴，甚则连及巅顶，头脑如空，经常眩晕，右侧耳鸣、耳聋。神疲乏力，畏寒肢冷，记忆力明显减退，饮食差，大便时有溏薄，月经量少色淡', '头痛，眩晕，耳鸣，耳聋，乏力，畏寒，肢冷，健忘，纳差，便溏，月经量少', '头痛', '头痛', '中医内科', '5剂,每日一剂,水煎', '补肾填精、祛风止痛法', '补肾祛风止痛汤加减', '蒸首乌15g,山药20g,山萸肉10g,杞果10g,当归10g,川芎10g,杜仲10g,续断10g,菊花10g,蔓荆子10g,白芷10g,细辛5g,天麻10g,甘草3g。', '注意休息，勿过劳。慎起居，避风寒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (140, '李振华补益气血、调和营卫法治疗颤振', 2, '郭某', '64', NULL, '男', '19911209', '体颤、伴发冷１月余', '者于1月前，参加体育比赛时，由于天气突然变冷，遂出现浑身发冷、打颤等症状，呈间断性发作，不能自制。经河南省人民医院诊治，诊断为交感神经过敏，给予抗过敏药物治疗（具体药物不详），效果欠佳。后又至河南医科大学附属医院，诊疗方案同前，疗效差。后经人介绍，求李老诊治。现症见自觉体颤、浑身发冷。多在上午左右，每次持续时间大约3～4小时不等。发作之后浑身汗出，精神困乏，饮食减少，眠差梦多，少气懒言。', '体颤，发冷，汗出，神疲，少气，懒言，纳差，寐差，多梦', '颤振', '神经官能症', '中医内科', '9剂,每日一剂,水煎', '补益气血、调和营卫法', '四君子汤,黄耆桂枝五物汤', '黄芪30g,红参6g（先煎）,白术10g,茯苓15g,桂枝6g,白芍15g,远志10g,酸枣仁15g,菖蒲10g,附子10g,山萸肉15g,防风5g,当归10g,丹参15g,炙甘草6g,生姜,大枣为引。', '慎起居，避风寒。畅情志，勿过劳', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (141, '李振华健脾疏肝、清心豁痰法治疗郁证', 2, '王某', '28', NULL, '女', '19911018', '胸部闷塞、烦躁、失眠10年，加重 2年', '患者近10年常无名状出现烦躁易怒，情绪不能自制。夜间入睡困难，常惊恐易醒，第2天全身疲乏无力。近2年来上述症状加重，心急烦躁，悲痛欲哭，不能自控，失眠多梦，胸闷气短，两胁窜痛，记忆力减退，体困乏力。常心存恐惧，觉生活没趣，几欲自杀未遂，食欲差，整夜不能入眠。到本地医院检查，未见异常。曾经多方治疗，效果一直不佳，后经人介绍求李老诊治。现症见头晕头痛，胸闷气短，心烦易怒，时有悲痛欲哭，惊恐不安，胸胁胀痛，全身乏力，周身窜痛，饮食欠佳，眠差多梦', '头晕，头痛，胸闷，气短，心烦，易怒，惊恐，胸痛，乏力，身痛，纳差，寐差，多梦', '郁证', '其他神经症性障碍,脏躁（脾虚肝郁）', '中医内科', '15剂,每日一剂,水煎', '健脾疏肝、清心豁痰法', '清心豁痰汤加减', '白术10g,茯苓15g,橘红10g,半夏10g,胆南星10g,香附10g,郁金10g,节菖蒲10g,栀子10g,莲子心5g,龙骨15g,琥珀3g（分两次冲服）,甘草3g', '畅情志，心理疏导。清淡饮食、忌食辛辣油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (142, '李振华补益心脾，安神定悸法治疗心悸', 2, '王某', '43', NULL, '女', '0', '心慌不安1月余', '近月来，时感心慌不安，睡前加重，时不能寐，站起时两眼发黑，偶有晕倒。面色恍白，食后腹胀，大便溏薄，睡前两下肢肿胀，以脚踝以下较重', '心慌，寐差，面白，腹胀，便溏，浮肿', '心悸', '冠心病', '中医内科', '20剂,每日一剂,水煎', '补益心脾，安神定悸法', '归脾汤', '黄芪20g,党参10g,白术10g,云苓15g,当归10g,川芎10g,远志10g,柏子仁15g,枣仁15g,桂枝3g,五味子12g,甘草6g。', ' 慎起居，避风寒，勿劳累。清淡饮食、忌烟酒辛辣油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (143, '李振华健脾疏肝，化痰降气法治疗郁证', 2, '魏某', '39', NULL, '女', '19870412', '咽中似有异物梗阻已半年', '患者咽中似有异物梗阻，吐之不出，咽之不下已半年，伴脘胁胀满，纳差嗳气，多疑善虑，神疲乏力。经多方医治，病情时轻时重，每因饮食不节或或情志不遂则症状加重。', '咽梗，胁胀，纳差，嗳气，多疑，神疲，乏力', '郁证', '癔球（咽部神经官能症）。', '中医内科', ' 6剂,每日一剂，分两次口服,水煎服', '健脾疏肝，化痰降气法', '理气消梅汤加味', '党参10g,白术10g,茯苓15g,橘红10g,半夏10g,广木香6g,砂仁8g,厚朴10g,枳壳10g,郁金10g,紫苏6g,佛手12g,桔梗10g,甘草3g', '忌食辛辣、海鲜食品，尽量保持心情舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (144, '李振华宣肺散寒，祛痰平喘法治疗哮喘', 2, ' 梁某', '43', NULL, '男', '19850305', '咳喘痰鸣已二个月。', '患者自述二个月前因扫雪受凉致感冒发热，咳嗽，服治疗感冒药一周，体温降至正常，但咳嗽不减，且逐渐加重，遂住入当地县医院治疗，胸片提示：支气管炎合并感染。用青、链霉素、氨茶硷等药治疗一周，病情有所控制而出院。出院后第三日病情再次加重，胸闷气短，喘息不得卧，喉间痰鸣，复用抗生素及平喘药效果不显，经省人民医院复查确诊为支气管哮喘。现喘促胸闷，不能平卧，畏寒肢冷，咳嗽气逆，吐痰稀白涎沫，量多，喉痒，腹胀纳差', '咳喘，痰鸣，畏寒，肢冷，喉痒，腹胀，纳差', '哮喘', '支气管哮喘,', '中医内科', '3剂,每日一剂，分两次口服,水煎服', '宣肺散寒，祛痰平喘法', '小青龙汤,二陈汤', '麻黄5g,前胡10g,细辛5g,干姜5g,五味子10g,苏子10g,桔梗10g,杏仁10g,陈皮10g,半夏10g,云苓15g,炙桑白皮12g,炙冬花10g,甘草3g。', '避风寒，勿食生冷油腻辛辣甜食。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (145, '李振华补肺健脾益气，止咳平喘化痰法治疗咳嗽,喘证,心悸', 2, '冯某', '30', NULL, '男', '19931108', '咳嗽喘息伴心慌三年，加重一周。', '患者自述于1990年冬季感冒，缠绵月余始愈，随即出现咳嗽、喘息、心慌等症状。每年夏季轻，冬季重。经当地市级医院诊断为肺源性心脏病，给予氨茶硷、心律宁、舒喘灵气雾剂等西药治疗，病情时轻时重。今年入冬以来，因天气寒冷自觉上述症状加重，一周来经服用以上药物疗效不佳。现咳嗽喘息、心悸气短，劳则加重，痰涎壅盛，恶风易汗，神疲乏力', '咳嗽，喘息，心悸，气短，多痰，恶风，自汗，神疲，乏力', '咳嗽,喘证,心悸', '慢性肺源性心脏病', '中医内科', '20剂,每日一剂，分两次口服,水煎服', '补肺健脾益气，止咳平喘化痰法', '四君子汤,三拗汤', '党参15g,白术9g,云苓15g,桂枝6g,半夏9g,炒远志9g,炒枣仁15g,节菖蒲9g,苏子9g,桔梗9g,杏仁9g,白果9g,炙麻黄5g,炙甘草5g', ' 调情志，避风寒，忌食生冷油腻辛辣之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (146, '李振华温肾健脾，益气升阳法治疗泄泻', 2, '刘某', '43', NULL, '女', '19900521', '泄泻15年，加重5年。', ' 患者自1975年开始泄泻，服多种抗生素月余，泄泻停止。后每到夏季或休息不好, 过于劳累即泄泻、腹部胀痛，虽未间断应用中西药物，但泄泻未愈。1985年冬季因出差野外受寒，出现水泻，时轻时重持续至今，重时日7～8次，轻时1～2次。去年10月在省某医院作结肠镜检查，发现距肛门15cm处肠粘膜充血水肿明显，诊断为“慢性结肠炎”。现仍泄泻，左下腹部疼痛，肠鸣，肛门有下坠感，畏寒肢冷，食少腹胀', '泄泻，腹痛，肠鸣，肛坠，畏寒，肢冷，腹胀，纳差', '泄泻', '慢性结肠炎,', '中医内科', ' 7剂,每日一剂，分两次口服,水煎服', '温肾健脾，益气升阳法', '黄芪建中汤、补中益气汤合附子理中汤', '黄芪15g,党参12g,白术10g,茯苓18g,桂枝5g,白芍10g,柴胡6g,升麻6g,薏苡仁30g,诃子肉12g,砂仁8g,陈皮10g,泽泻12g,炮姜5g,附子10g,炙甘草6g,生姜10g,大枣5枚。', ' 避风寒 ，忌食生冷油腻食品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (147, ' 李振华健脾益气、化痰通络法治疗心悸', 2, '李某', '42', NULL, '男', '19880416', '因感冒发烧致心悸，胸中闷痛已月余', '患者月余前因感冒发烧继发心慌、胸中闷痛，经河南医科大学一附院确诊为病毒性心肌炎，服用慢心律等药效果不显而来就诊。查体温、抗“O”正常，心率86次∕分，早搏12次∕分。心电图示频发性早搏，呈二联律，下壁心肌缺血。现心慌不能自抑，胸中憋闷窒塞，时有胸痛，头晕，神疲乏力，食欲不振，恶心欲吐。', '心悸，胸闷，胸痛，头晕，神疲，乏力，纳差，恶心，欲吐', '心悸', '病毒性心肌炎', '中医内科', '10剂,每日一剂，分两次口服,水煎服', '健脾益气、化痰通络法', '加味四君子汤', '党参20g,白术10g,茯苓15g,薏苡仁15g,广木香10g,枳壳12g,橘红10g,半夏10g,桂枝6g,节菖蒲15g,丹参15g,炙甘草10g。', '勿劳累，注意休息，保持心情舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (148, '李振华益气补血，健脾调经法治疗月经不调', 2, '马某', '53', NULL, '女', '19930427', '此次月经淋漓不尽15天', '1992年3月始，每次月经经期延长均达12天以上。此次月经于本月12日来潮至今未净，数次去多家医院就诊，西医诊断为功能性子宫出血，服用西药(药物不详)及经本单位职工门诊治疗，效果不佳。现月经淋漓不断，小腹空坠隐痛，伴有自汗，周身倦怠乏力，腰困酸痛，睡眠欠佳。  舌质： 质淡，体胖大', '月经淋断，腹痛，自汗，倦怠，乏力，腰困，寐差', '月经不调', '功能性子宫出血', '妇科', ' 5剂,每日一剂，分两次口服。水煎服', '益气补血，健脾调经法', '加减归脾汤', '黄芪30g,党参12g,白术10g,云苓15g,当归10g,白芍12g,炒枣仁10g,川断10g,杜仲10g,炒艾叶10g,丹参10g,阿胶10g,炙甘草6g,天麻12g,细辛5g', '注意经期卫生，忌食生冷肥甘辛辣之品，避免劳累，慎起居。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (150, ' 李振华益气养血，活血安神法治疗胸痹', 2, '李某', '29', NULL, '男', '0', '胸闷痛、心悸4年余', '4年前始出现胸闷痛、心慌、气短。时于本县医院查ECG提示：心脏后壁供血不足，后壁陈旧性梗塞。窦性心动过缓，左室高电压，左心室肥大等。经湖北医学院，河南医科大学一、二附属医院，北京埠外医院检查初步诊断：冠心病。经治疗效不佳（具体治疗药物不详），病情时轻时重、未能根治。现在症见胸前区阵发性闷痛（多于上午8AM～9AM及下午发作）、心慌、气短，全身乏力，精神差，食欲尚好，二便正常', '胸闷，胸痛，心慌，气短，乏力', '胸痹', '无,冠心病', '中医内科', '7剂,每日一剂,水煎 ', '益气养血，活血安神法', '炙甘草汤', '红参6g,麦冬15g,生地15g,丹参15g,阿胶10g,桂枝3g,云苓15g,远志10g,枣仁15g,菖蒲9g,川芎10g,甘草6g。', '慎起居，避风寒，勿劳累。清淡饮食，忌辛辣油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (151, '李振华祛痰利湿、熄风通络法治疗中风', 2, '和某', '42', NULL, '男', '19910409', '双下肢麻木乏力半年余', '半年前无明显诱因出现两腿麻木伴困乏无力，行走不能。初始可依靠拐杖自己行走，现已不能下床。曾于当地县医院以及解放军某医院住院治疗，均诊断为多发性神经炎，进行性肌无力等，经过治疗效果不佳（具体用药不详）。后经人介绍，来我院求李老诊治。患者体质肥胖，就诊时神志清晰，回答清楚准确，不能行走，自觉双下肢发麻，全身乏力，平时口角流涎不能自制，半年来，食欲不佳', '肢麻，流涎，乏力，纳差', '中风', '不详', '中医内科', '6剂,每日一剂,水煎 ', '祛痰利湿、熄风通络法', '祛湿通络汤', '白术9g,茯苓15g,橘红10g,半夏10g,泽泻10g,菖蒲10g,黄芩10g,地龙20g,鸡血藤30g,木瓜20g,乌梢蛇15g,蜈蚣3条,甘草3g', '加强适度肢体功能锻炼，注意休息。清淡饮食，忌辛辣油腻食物。及时服药', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (152, ' 李振华化痰行瘀，活血降法治疗噎膈', 2, '卞某', '39', NULL, '男', '0', '胸痛吞咽不畅20余天', '素有胃病史，常在食后胃脘疼痛半小时左右，曾在当地医院做胃镜检查，诊为慢性胃炎，用西药对症治疗，病情时轻时重，一直未曾根治。上月中旬，忽然当胸而痛，进食梗噎不畅，近日病情加重，吞咽困难，仅能进流质食物，5天前在某医院作检查，发现食道有痉挛狭窄，特请李老诊治。', '胸痛，咽梗', '噎膈', '1、慢性胃炎；2、食道痉挛。', '中医内科', '20剂,每日一剂，分两次口服,水煎服', '化痰行瘀，活血降法', '二陈汤合旋复代赭汤加减。', '白术10g,陈皮10g,半夏10g,云苓15g,丹参20g,桃仁10g,红花10g,沉香5g,旋覆花10g,代赭石20g,枳实10g,郁金10g,川黄连5g,干姜6g,甘草3g', '忌生冷油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (153, ' 李振华温胃散寒，降逆止呃法治疗呃逆', 2, '王某', '64', NULL, '男', '19920403', '呃声连连近月', '上月某日，家中来客，备菜满桌，待客以后，所剩菜多。次日适逢爱人有事外出，一人在家，食凉菜数盘，始觉脘腹微痞不舒，继则呃逆连声不止，大便微溏，曾到当地医院检查，诊断膈肌痉挛，西药对症治疗，服药时症状亦能微减，过后则呃逆连声如前。现脘腹痞满不舒，呃声连连不休而来求诊', '腹胀，呃逆，便溏', '呃逆', '膈肌痉挛', '中医内科', '3剂,每日一剂，分两次口服,水煎服', '温胃散寒，降逆止呃法', '丁香柿蒂汤,理中汤,良附丸', '丁香5g,柿蒂15g,党参10g,白术10g,云苓15g,陈皮10g,蔻仁10g,良姜10g,香附10g,乌药10g,西茴10g,甘草6g,生姜三片为引', '忌生冷、油腻食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (154, '李振华温中散寒，疏肝和胃法治疗呕吐', 2, '董某', '47', NULL, '男', '19921019', '胃脘痛五日，昨日起恶心呕吐', '胃脘疼痛已多年，时好时坏，经当地医院诊为慢性胃炎，犯病时服维生素U，普鲁苯辛一类药物对症治疗。这次胃脘痛已5日，用西药无效，疼痛不减，晨起忽增呕吐而来就诊。', '胃痛，恶心，呕吐', '呕吐', '慢性胃炎,胃神经官能症,', '中医内科', '5剂,每日一剂，分两次口服,水煎服', '温中散寒，疏肝和胃法', '附子理中汤,六君子汤,左金丸', '陈皮10g,半夏10g,苏梗10g,枳实10g,附子6g,干姜10g,吴茱萸5g,黄连6g,槟榔10g,茯苓15g,白术10g,丁香5g,甘草3g。', '忌生冷，油腻食品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (155, '李振华疏肝理脾，和胃止痛法治疗胃脘痛', 2, '张某 ', '37', NULL, '男', '0', '胃脘疼痛多年，近因劳累过度加重。', '素有胃脘疼痛病史，近因劳累过度。加之饮酒过量，胃脘疼痛发作，胃脘胀痛，痛连两胁，走窜不定，嗳气频作，胸闷痞满，纳谷不香，体困乏力，情志抑郁，烦躁易怒，大便溏薄。', '胃痛，胸痛，嗳气，胸闷，纳差，乏力，抑郁，烦躁，易怒，便溏', '胃脘痛', '慢性胃炎', '中医内科', '5剂,每日一剂，分两次口服,水煎服', '疏肝理脾，和胃止痛法', '柴胡疏肝散', '柴胡6g,枳壳10g,杭白药15g,香附10g,白术10g,云苓15g,陈皮10g,元胡10g,川厚朴10g,砂仁8g,川芎10g,甘草3g。', '保持情志舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (156, '李振华理气健脾，温中和胃法治疗胃脘痛', 2, ' 薛某', '41', NULL, '女', '19920110', '胃痛纳差两月余', ' 20多年前曾经患过胃病，胃脘疼痛、腹满，食欲不振，大便日行数次，打嗝，嗳气频，后到北京请秦伯未老先生予以诊治，病情得到了控制。20多年来，只要犯胃病，就服秦老所开处方：紫苏梗、白蒺藜、炒枳实、制香附、煨木香、大腹皮、元胡索、金铃子、瓜蒌仁。最近两个月来胃痛，纳差，每天只能吃半斤左右，早饭和晚饭一点不敢多食，嗳气频作，大便日行1-2次，心烦急躁，易怒，夜梦多，有时不易入睡，胃痛波及全胸，疼痛游窜不定。服上药效果不佳，故来求诊', '胃痛，胸痛，纳差，嗳气，心烦，急躁，易怒，多梦，寐差', '胃脘痛', '慢性胃炎', '中医内科', '10剂,每日一剂，分两次口服,水煎服', '理气健脾，温中和胃法', '自拟香砂温中汤加味', '白术10g,云苓15g,橘红,半夏各10g,香附10g,砂仁8g,西茴10g,乌药10g,枳壳10g,丁香5g,柿蒂15g,广木香6g,元胡10g,白芍15g,龙骨15g,泽泻12g,甘草3g。', ' 1、注意饮食，忌食辛辣、生冷和油腻之品；2、注意保持心情愉快。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (157, '李振华清肝泻火，和胃止痛法治疗胃脘痛', 2, '张某 ', '49', NULL, '男', '19920220', '胃脘疼痛年余，加重半月', '患者去年年初开始胃脘疼痛，吞酸嘈杂，纳呆消瘦，大便偏干，小便略黄，经胃镜检查，诊为胃窦炎，曾住院治疗，病情得到控制而出院。后又多次复发，用药后症状可暂时缓解。半月前，胃痛忽然加重而就诊。', '胃痛，吐酸，嘈杂，纳呆，消瘦，便干', '胃脘痛', '胃窦炎', '中医内科', '12剂,每日一剂，分两次口服,水煎服', '清肝泻火，和胃止痛法', '左金丸,柴胡疏肝散', '姜黄连10g,吴茱萸5g,柴胡6g,杭白芍15g,青皮10g,川楝子10g,枳实10g,龙胆草10g,栀子10g,黄芩10g,知母10g,竹茹10g,甘草3g。', '1、保持情志舒畅。2、忌辛辣刺激食物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (158, '李振华滋阴养胃，舒肝理气，和胃止痛法治疗胃脘痛', 2, '曹某', '62', NULL, '男', '0', '胃脘疼痛十年余，加重2年', ' 患者胃脘疼痛十年有余，近两年来发作频繁，病势加重。年初曾到某医院做胃镜检查并治疗，诊断为“胃窦炎”，经用西药治疗，病情不见好转，特求中医治疗。现胃脘疼痛胀满。', '胃痛', '胃脘痛', '胃窦炎', '中医内科', '12剂,每日一剂，分两次口服,水煎服', '滋阴养胃，舒肝理气，和胃止痛法', '沙参养胃汤', '北沙参15g,麦冬10g,生地10g,当归10g,石斛10g,杭白芍15g,枳壳10g,陈皮10g,元胡10g,砂仁10g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g。', ' 1、忌辛辣、油腻。服清淡食物，宜少食多餐。2、保持心情舒畅。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (159, ' 李振华滋阴平肝，祛湿通络法治疗眩晕', 2, '温某', '43', NULL, '男', '19780323', '高血压病，发现高血脂症4年。', '患者自1974年4月份发现高血脂症，当时检查胆固醇420毫克（正常值：100——230mg%），甘油三脂1050毫克（正常值：355mg%），β脂蛋白1546毫克（正常值<700mg%）。经过不断服用中西药物治疗，效果不明显。血压波动在130～175/100～110mmHg，身体肥胖。经常头晕，下肢沉重', '头晕，肢重', '眩晕', '1、高血压病；2、高血脂症。', '中医内科', '33剂,每日一剂，分两次口服,水煎服', '滋阴平肝，祛湿通络法', '滋阴清肝汤加减', '蒸首乌21g,川牛膝15g,赤芍15g,泽泻12g,荷叶30g,草决明15g,山楂15g,地龙21g,鸡血藤30g,丹参21g,全蝎9g,桃仁15g,节菖蒲9g,牡蛎15g。', '忌肥甘油腻，食清淡食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (160, '李振华理气祛痰，清热安神法治疗不寐', 2, '彭某 ', '63', NULL, '男', '19790312', '失眠三个月。', '患者于1978年12月中旬，因在郑州市某门诊部医生误诊为肝硬变，思想紧张，以致当天即失眠，整夜不眠。后在某医院住院两个月，服多种镇静安眠药如异丙嗪、速可眠、水化氯醛等，方能入睡3～4个小时，后再用药治疗无效出院，特来求诊。患者在医院住院诊断结果：1、右束枝完全性传导阻滞。2、类风湿性关节炎。3、高血压。4、慢性肝炎。5、神经衰弱', '失眠', '不寐', '神经衰弱,高血压,慢性肝炎,', '中医内科', '8剂,每日一剂，分两次口服,水煎服', '理气祛痰，清热安神法', '黄连温胆汤', '陈皮9g,半夏9g,云苓15g,胆南星9g,枳壳9g,栀子9g,莲子心6g,黄连6g,元桂3g（另冲）,合欢皮24g,夜交藤30g,龙骨15g,淡竹叶15g,竹茹12g,甘草3g。', '心情保持舒畅。忌辛辣、刺激性食物，宜服清淡食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (161, '李振华活血通络，舒肝息风法治疗痉证', 2, '邓某', '28', NULL, '男', '19781120', '右上肢痉挛1个月', '患者因于1978年10月份和同龄孩童打架，右上肢被扭伤，随即出现右上肢持续性痉挛，伴有疼痛酸困。曾在省某医院检查诊断为臂丛神经痉挛，经治无效，而来求诊。', '肢痛', '痉证', '臂丛神经痉挛', '中医内科', '25剂,每日一剂，分两次口服,水煎服', '活血通络，舒肝息风法', '黄芪四物汤加减', '当归6g,川芎6g,白芍10g,丹参15g,鸡血藤18g,地龙12g,桑枝24g,秦艽6g,黄芪12g,磁石18g,全蝎6g,节菖蒲6g,龙骨12g,牡蛎12g,甘草3g。', '勿做剧烈运动。右上肢做适当活动', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (162, ' 李振华健脾益气，和胃化湿法治疗泄泻', 2, '钟某', '43', NULL, '女', '19910926', '腹泻时愈时发两年余', '1989年6、7月份出现腹泻，大便日行3-5次，夹有不消化食物，脘腹胀满不舒，时有嗳气，在某医院作钡剂灌肠检查，诊断为“慢性结肠炎”，给服土霉素、四环素治疗。腹泻虽减，而腹胀纳呆不去，特请中医辨证治疗。现仍溏便，日行2-3次，有不消化食物，脘腹胀满 ，纳呆神疲，四肢困重，下肢有时浮肿，以劳累后为甚', '腹胀 ，纳呆，神疲，浮肿，便溏，四肢困重', '泄泻', '慢性结肠炎,', '中医内科', '6剂,每日一剂，分两次口服,水煎服', '健脾益气，和胃化湿法', '加减参苓白术散', '党参12g,白术10g,云苓15g,扁豆10g,山药15g,陈皮10g,半夏10g,苡仁20g,砂仁8g,藿香10g,白芍12g,甘草3g,焦山楂12g,焦神曲12g,焦麦芽12g,木香6g。', '忌食生冷、油腻、不易消化的食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (163, '李振华补中益气，甘温除热法治疗泄泻', 2, '许某', '57', NULL, '女', '19920423', '反复泄泻3年余，加重伴肛门下坠、发热一年。', '患者反复泄泻3年余，近一年来，腹泻频繁，甚则水泻无度，肛门下坠，伴有发热，体温持续在38℃～38.5℃之间，经某医院结肠镜检查，诊断为慢性结肠炎，功能性低热。曾经用中西药物治疗，未见明显效果。现在腹泻，每日4～5次，体温38.5℃，腹胀隐痛喜按，口燥咽干，渴不多饮，食欲欠佳，神疲乏力，气短懒言。', '腹泻，发热，腹胀，口燥，咽干，纳差，神疲，乏力，气短，懒言', '泄泻', '慢性结肠炎,功能性低热', '中医内科', '7剂,每日一剂，分两次口服,水煎服', '补中益气，甘温除热法', '加减补中益气汤', '党参15g,黄芪20g,白术12g,云苓15g,升麻6g,柴胡6g,木香6g,砂仁8g,葛根9g,川黄连5g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g', '忌食生冷、油腻等物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (164, '李振华补中益气，升举清阳法治疗腹胀,脱肛', 2, '徐某 ', '43', NULL, '女', '19910912', '脘腹胀满十年余', '1980年初因脘腹胀满，大便不爽，久坐努责，肛门重坠到某医院检查，诊断为肠结核，采用对症疗法治疗，病情时轻时重，一直没能根治。现在脘腹胀满，食后尤甚，大便不爽，虚坐多时，肛门下坠，身困乏力，纳食日减。', '腹胀，大便不爽，肛坠，身困，乏力，纳差', '腹胀,脱肛', '肠结核', '中医内科', ' 5剂,每日一剂，分两次口服,水煎服', '补中益气，升举清阳法', '加减补中益气汤,', '黄芪20g,党参10g,白术10g,陈皮10g,半夏10g,枳壳10g,木香6g,升麻6g,沉香曲10g,炮姜炭5g,砂仁8g,川朴10g,炙甘草5g。', ' 保持情志舒畅，忌食生冷油腻', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (165, '李振华疏肝解郁、理气止痛法治疗腹痛,聚证', 2, '张某', '41', NULL, '女', '19920315', '脐两侧时痛年余', '1991年腊月间出现脐两侧疼痛，有时有硬块，遂到当地医院检查治疗。B超检查无异常，妇科检查正常即按炎症对症治疗。曾经服用西药，效果不佳，特来求治中医。现在自觉脐两侧有硬块，时疼痛发生，同时胃脘也痛，并上腹胀不舒，待矢气痛胀减轻，而情志不舒则痛胀加重。食欲下降，饮食减少，体倦乏力，偶尔出现头晕心悸', '脐两侧时痛，胃痛，腹胀，纳差，体倦乏力，头晕，心悸', '腹痛,聚证', '腹痛,胃肠炎', '中医内科', '7剂,每日一剂，分两次口服,水煎服', '疏肝解郁、理气止痛法', '自拟方', '乌药10g,沉香6g,槟榔10g,枳实10g,木香6g,柴胡6g,白芍15g,川芎10g,元胡10g,砂仁8g,香附10g,甘草3g', ' 调畅情志，保持心情舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (166, '李振华健脾利湿、温中散寒法治疗腹痛,泄泻', 2, ' 郭某', '52', NULL, '女', '19920811', '脐腹疼痛2月余。', ' 六月初因贪食生冷，致腹痛泄泻，日行2-3次，往某医院诊断为肠炎，给予西药对症治疗。现已两月余，病情时轻时重，一直未能彻底根治。脐腹疼痛，一痛即泻，日行2-3次，脘腹闷胀，肢体困倦，食欲欠佳。', '脐痛，泄泻，腹胀，困倦，纳差', '腹痛,泄泻', '慢性结肠炎', '中医内科', '5剂,每日一剂，分两次口服,水煎服', '健脾利湿、温中散寒法', '加味四君子汤', '党参10g,白术10g,云苓15g,泽泻10g,肉桂10g,炮姜10g,吴茱萸6g,川朴10g,砂仁8g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g,苡仁30g', ' 忌食生冷油腻，饮食清淡富于营养', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (169, '李振华消食和胃，疏肝健脾法治疗胃痞', 2, '许某', '27', NULL, '男', '19930109', '胃脘痞塞满闷年余', '去年以来，因饮食所伤经常出现胃脘满闷不适，食后胀甚，曾在当地医院检查治疗，服用维生素B1、谷维素、大黄苏打片、三黄片及中药等，效均不佳，致使病情迁延至今，现仍脘腹胀满痞塞，食后痞塞胀甚，甚则吐酸水、食欲差、嗳气多、腹胀拒按，每日早上入厕，大便稀，背部沉困不舒。 ', '腹胀，吐酸，嗳气，便稀，背沉', '胃痞', '1、消化不良；2、胃神经官能症。', '中医内科', '5剂,每日一剂，分两次口服,水煎服', '消食和胃，疏肝健脾法', '香砂温中汤', '炒山楂15g,炒麦芽15g,神曲10g,莱菔子10g,半夏10g,陈皮10g,云苓15g,川朴10g,白术10g,木香6g,砂仁8g,甘草3g。', '忌辛辣油腻、勿过食过饱', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (171, ' 李振华益气健脾、温中和胃法治疗腹痛', 2, '张某', '26', NULL, '男', '19920309', '腹痛5日。', '5日前不明原因出现脐腹隐隐作痛，绵绵不断，喜热喜按，得温得按痛减，2天后出现大便稀溏，日行2-3次，饮食减少，食后腹胀。', '腹痛，便溏，腹胀', '腹痛', '慢性肠炎', '中医内科', ' 5剂,每日一剂，分两次口服,水煎服', '益气健脾、温中和胃法', '自拟方', '党参10g,白术10g,云苓15g,泽泻10g,桂枝6g,白芍15g,砂仁8g,苡仁30g,煨肉蔻10g,诃子肉10g,炙甘草6g,干姜10g,生姜三片,大枣5枚为引。', '忌食生冷、油腻之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (172, '李振华健脾益气、祛寒通阳、理气燥湿、涩肠止痢法治疗痢疾', 2, '钟某', '61', NULL, '女', '20050830', '大便带有粘冻伴里急后重时常发作11年', ' 患者于1994年夏季麦收时因一次过食生冷菜肴，致腹痛，痢下粘条，便意未尽感，即去当地卫生院静滴及口服西药，症状消失停止治疗。大约一周后因饮食不慎致病情复发，即与上述治疗而愈。此后因饮食原因致病情时发时愈。曾到当地县医院、郑州市及开封市一些医院多次治疗，经应用中西药物，病情始终未有痊愈。现大便日3－4次，大便前小腹疼痛，有里急后重感，便中伴有粘冻，乏力，时常头晕。', '腹痛，腹泻，痢下粘冻，乏力，头晕', '痢疾', '慢性溃疡性结肠炎', '中医内科', '15剂,口服,水煎 ', '健脾益气、祛寒通阳、理气燥湿、涩肠止痢法', '温中止痢汤', '白术15g,苍术10g,茯苓15g,炒薏苡仁30g,陈皮10g,半夏10g,香附10g,木香6g,厚朴10g,乌药10g,砂仁8g,西茴10g,吴茱萸5g,桂枝5g,诃子12g,白芍12g,甘草3g。', '忌食生冷油腻，注意饮食卫生，勿劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (173, '李振华健脾益气，疏肝理气，温中散寒法治疗胃痞', 2, '季某', '55', NULL, '女', '20050521', '上腹部时常胀痛20年', ' 患者于20年前因饮食不节，加之劳累及情志不畅致上腹部胀满疼痛，伴恶心呕吐，在当地县医院治疗，先后服用中药汤剂30余剂（具体用药不祥），以及香砂养胃丸、三九胃泰冲剂、丽珠得乐、复发胃友片、维酶素片等，症状时轻时重，2004年4月17日作胃镜检查提示为：慢性萎缩性胃炎，返流性食管炎（Ⅰ度）。现胃脘胀满，时有胃痛，泛酸，纳少，乏力', '胃胀，胃痛，泛酸，纳少，乏力', '胃痞', '慢性萎缩性胃炎，返流性食管炎（Ⅰ度）', '中医内科', '30剂,口服,水煎', '健脾益气，疏肝理气，温中散寒法', '香砂温中汤加减', '党参12g,白术10g,云苓12g,陈皮10g,半夏10g,香附10g,川朴10g,乌药10g,木香6g,沉香5g,西茴10g,吴茱萸3g,桂枝5g,刘寄奴15g,砂仁8g,白蔻仁10g,郁金10g,白芍12g,甘草3g。', ' 饮食规律，忌食肥甘厚味、辛辣生冷之品，注意腹部保暖及保持心情舒畅。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (174, ' 李振华健脾益气，温中散寒，疏肝理气，和胃止痛法治疗胃脘痛', 2, '赵某', '56', NULL, '女', '20050903', '胃脘疼痛近一个月', '2005年4月6日因大便秘结服攻下润肠、疏肝理气之中药汤剂12剂后引起胃痛，即停服中药到乡医院静滴（用药不详），口服复方胃友片等西药，胃痛缓解，但未完全消失，此后至今胃脘钝痛阵作，饮食生冷则胃痛加重，食欲不振，食量减少，大便成形，不干结', '胃痛，纳差', '胃脘痛', '慢性浅表性胃窦炎。', '中医内科', '20剂,口服,水煎 ', '健脾益气，温中散寒，疏肝理气，和胃止痛法', '香砂温中方加减', '党参15g,白术10g,云苓12g,陈皮10g,半夏10g,香附10g,川朴10g,乌药10g,木香10g,西茴10g,吴茱萸6g,干姜5g,桂枝5g', '忌食辛辣生冷及不易消化食品，注意饮食有节及腹部保暖', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (175, '李振华健脾养肝、祛湿活血、潜降熄风法治疗眩晕', 2, '马某', '36', NULL, '女', '20050820', '眩晕耳鸣、体倦乏力已三年余', '患者自述于2002年5月份开始感觉眩晕，耳鸣，时觉头沉，体倦乏力，但未引起重视。2003年初开始病情加重，乃至洛阳市第一人民医院诊治，经检查确诊为高血压、高血脂症，经服西药维压静、舒降之等药血压下降，上述症状有所减轻。后至洛阳市中医院给予育阴潜阳、健脾利湿等中药效果不显。现感头晕，耳鸣，头目胀痛沉重，每因劳累及心情不佳时加重，胸闷，恶心，周身困倦乏力', '头晕，耳鸣，头痛，胸闷，恶心，困倦，乏力', '眩晕', '高原性高血压,高血脂症。', '中医内科', '25剂,口服,水煎', '健脾养肝、祛湿活血、潜降熄风法', '平亢通络汤', '白术12g,茯苓10g,泽泻10g,节菖蒲10g,川牛膝9g,女贞子15g,荷叶30g,草决明12g,全蝎9g,牡蛎15g,赤芍10g,山楂15g,地龙21g,鸡血藤30g,丹参20g,桃仁12g,甘草5g', '嘱忌食肥甘油腻及不易消化食品，适当锻炼，尽量保持心情舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (176, ' 李振华温经散寒，健脾除湿，通经活络法治疗痹证', 2, '刘某', '45', NULL, '女', '20041215', '关节疼痛肿胀已1年半', ' 2003年5月间因居处新房潮湿，复感雨淋，致左髋关节疼痛，当时未予重视。两个月后指、肘、腰椎、膝、两踝等关节均疼痛重着，指、膝、踝关节尚伴有肿胀，遇寒加重，活动不便。至今年8月份诸关节疼痛愈甚，终日卧床，转侧困难，无法行走，在区、市级医院服中药汤剂两百余剂，并配合针灸，效果不显，静滴抗菌素及口服炎痛喜康等西药可取暂时之效。现身体发沉，手、胳膊、两腿屈伸都不灵活。在床上翻身都不容易，走路也很困难，一受凉就更重，关节处整天都觉得冰凉', '身重，关节痛', '痹证', '风湿性关节炎', '中医内科', '6剂,口服,水煎 ', '温经散寒，健脾除湿，通经活络法', '温通除痹汤', '白术20g,茯苓18g,泽泻12g,桂枝9g,防己15g,香附12g,制川乌5g,千年健15g,苍术10g,黄柏5g,山甲10g,木瓜18g,薏苡仁30g,制马钱子1g,甘草3g', ' 避免风吹，注意防寒防潮，起居作息有规律', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (177, '李振华健脾疏肝，清化痰热，养阴利咽法治疗梅核气', 2, '石某', '45', NULL, '男', '0', '咽中异物感一年余', '患者自述去年9月初感觉咽喉有异物梗阻，咯之不出，咽之不下，时有胸闷气短，夜间口干，每食辛辣、饮酒及心情不舒时加重，经郑州市第五人民医院五官科检查确诊为慢性咽炎。经交替服用头孢拉啶、乙琥红霉素、罗红霉素、黄连上清丸等，病情稍轻。自购西瓜霜含片、金嗓子喉宝等，不适感严重时就自行应用', '咽梗，胸闷，气短，口干', '梅核气', '慢性咽炎,', '中医内科', ' 20剂,口服,水煎', '健脾疏肝，清化痰热，养阴利咽法', '理气消梅汤。', '白术10g,云苓15g,陈皮12g,半夏10g,香附10g,砂仁6g,西茴10g,乌药10g,川朴10g,桔梗10g,牛蒡子10g,山豆根10g,射干10g,麦冬15g,甘草3g', '忌食辛辣刺激食品，忌烟酒，自我调节情绪。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (178, '李振华健脾和胃，温运中阳，温化寒湿，理气除胀法治疗胃痞', 2, '刘某', '36', NULL, '女', '20060327', '胃脘胀满疼痛两月余', '患者自诉由于经常出差外地，饮食起居不规律，致胃脘胀满已近半年，因工作忙碌，自购健胃消食片、大山楂丸、吗叮啉等药服用，自觉胀满好转。两个月前因与人聚餐饮用冰镇啤酒而致胃脘胀满加重，服上述药物效果不佳。现胃脘胀满，饭后加重，时有隐痛，纳呆，乏力，胃脘喜暖，饮食温热食品觉胃脘舒适', '胃痛，纳呆，乏力', '胃痞', '慢性浅表性胃炎', '中医内科', '10剂,口服,水煎', '健脾和胃，温运中阳，温化寒湿，理气除胀法', '香砂温中汤加减', '白术10g,茯苓12g,砂仁8g,白蔻仁10g,陈皮10g,半夏10g,厚朴10g,香附10g,乌药10g,木香6g,沉香3g,甘松10g,萝卜种15g,桂枝5g,西茴10g,焦山楂15g,焦神曲15g,焦麦芽15g,甘草3g。', ' 饮食有节，忌食生冷不易消化食品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (179, '李振华疏肝健脾，凉血活血法治疗痛经', 2, '贾某', '55', NULL, '女', '0', '经期腹痛两年余', '患者自述于九岁时曾患胃脘疼痛，在某医院行胃镜检查未见异常，多年来反复发作，服用中西药治疗，时轻时重。两年前无明显原因出现经期少腹作痛，痛引腰骶部，在某医院检查诊断为子宫内膜炎，经治疗效不佳，每随月经而周期性发作，后经检查又示：盆腔炎、盆腔积液，在省妇幼保健院治疗，疗效仍不佳。现症见经期少腹疼痛拒按，痛引腰骶，月经量少，色黯黑有血块，经前乳房胀痛，心烦急躁，食欲不佳，眠寐及二便尚可', '痛经，月经量少，乳痛，心烦，急躁，纳差，寐差', '痛经', '继发性痛经', '妇科', ' 3剂,内服,水煎', '疏肝健脾，凉血活血法', '自拟活血止痛汤加减', '当归10g,白芍15g,白术10g,茯苓15g,柴胡6g,香附10g,西茴10g,乌药10g,公英15g,木香6g,生地炭15g,丹参15g,黑地榆12g,青皮10g,丹皮10g,甘草3g,元胡10g', '月经将来，少腹出现胀痛之时服药；保持情绪舒畅。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (180, '李振华疏肝健脾，理气清热法治疗胃痞', 2, '靳某', '30', NULL, '女', '20060422', '胃脘胀满三年余', '患者自述三年来每遇生气动怒等情绪过激及受凉后出现脊背正中如手掌大小阵发性跳动性发凉，继而周身发冷（自述体温正常），乏力，伴有胃脘胀满，灼热感，呃逆，泛酸，在当地医院输液及拔罐治疗稍缓解，但受凉及生气后又易加重，三年来反复发作。现症见胃脘胀满，情志不舒时加重，胃脘灼热感，时有嗳气，泛酸，口苦，口干多饮，时感头晕，纳差，怕食冷物，睡眠尚可，大便干，二日一次', '胃胀，胃灼，嗳气，泛酸，口苦，口干，头晕，纳差，便干', '胃痞', '慢性胃炎', '中医内科', ' 21剂,每日一剂,水煎', '疏肝健脾，理气清热法', '香砂温中汤加减', '白蔻仁10g,佛手12g,厚朴10g,知母12g,炒栀子10g,花粉12g,萝卜种20g,白术10g,茯苓12g,陈皮10g,半夏10g,香附10g,砂仁8g,桂枝5g,白芍12g,西茴10g,乌药10g,木香6g,郁金10g,甘草3g', '饮食宜清淡，忌食辛辣、生冷等物；情志宜舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (181, ' 李振华疏肝理气，清热和胃法治疗胃脘痛', 2, '于某', '31', NULL, '男', '0', '胃脘部疼痛，连及右侧胁肋部及右后背五年', '患者自述于五年前无明显原因出现胃脘疼痛，并连及右胁背部，在当地医院诊断为胆汁反流性胃炎，经治疗时轻时重。后在某医院检查患有胆囊息肉、胆囊炎，行胆囊摘除术。术后五个月后，胃脘痛等症状加重。经中西医治疗，效不佳。现症见胃脘胀痛，每遇情绪激动时症状加重，口干不苦，时有嗳气，平素急躁易怒，时有头晕，记忆力减退，纳差，怕食生冷等物，眠寐尚可，大便时溏', '胃痛，口干，嗳气，急躁，易怒，头晕，纳差，便溏', '胃脘痛', '慢性胃炎', '中医内科', '7剂,每日一剂,水煎', '疏肝理气，清热和胃法', '香砂六君子汤加减', '刀豆子15g,柿蒂15g,萝卜种15g,知母12g,花粉12g,川楝子12g,刘寄奴15g,白术10g,茯苓12g,陈皮10g,半夏10g,香附10g,砂仁8g,桂枝5g,白芍12g,西茴10g,乌药10g,木香6g,郁金10g,甘草3g', '饮食宜清淡；适量体育锻炼；情志宜舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (182, '李振华养阴和胃，理气清热法治疗胃痞', 2, '周某 ', '62', NULL, '女', '20060411', '胃脘胀满、纳呆、食欲不振6年', '患者自幼挑食，6年前出现纳呆、食欲不振，无饥饿感，进食六两/日，喜进稀饭，胃脘胀满，时有泛酸，经中西医治疗效不佳。现症见形体消瘦，无食欲，不知饥，纳少，胃脘胀闷，伴有泛酸，晨起脐下腹部隐隐作痛，持续20分钟，可自行缓解，睡眠及二便尚可', '胃胀，纳呆，消瘦，泛酸', '胃痞', '慢性胃炎', '中医内科', ' 7剂,每日一剂,水煎 ', '养阴和胃，理气清热法', '沙参养胃汤加减', '知母12g,花粉12g,辽沙参15g,石斛15g,郁金10g,乌药10g,鸡内金10g,陈皮10g,焦山楂12g,焦神曲12g,焦麦芽12g,萝卜种15g,甘草3g,麦冬12g,木香6g', '饮食宜清淡，情志宜舒畅，适当参加体力劳动及体育锻炼。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (183, '李振华健脾和胃，疏肝理气法治疗胃痞', 2, '逯某', '27', NULL, '男', '20050809', '胃脘满闷二年', '2003年无明显诱因觉胃脘满闷、灼热，去市一院查心电图无异常，查胃镜示：1.食管炎，2.反流性胃炎.HP+++经服奥美拉唑，阿莫西林，胃康胶囊，后又服中药（具体不详）半年，症状稍有减轻。2004年C14呼气试验提示HP转阴。近日复觉胃脘不适，于今日前来就诊，现症见：胃脘痞闷，饱餐后嗳气、大便不畅，2日1行，小便正常。', '胃胀，嗳气，大便不畅', '胃痞', '慢性胃炎,慢性食管炎,', '中医内科', '10剂,每日一剂，分两次口服,水煎服', '健脾和胃，疏肝理气法', '香砂温中汤加味', '白术10g,茯苓12g,陈皮10g,旱半夏10g,香附10g,砂仁8g,厚朴10g,枳壳10g,西茴10g,乌药10g,郁金10g,刀豆子15g,柿蒂15g,黄连6g,吴茱萸5g,甘草3g,瓦楞子15g,萝卜种15g', '调畅情志，规律饮食', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (184, '李振华温肾健脾，固肠止泻法治疗五更泄', 2, '郭某', '60', NULL, '男', '20050607', '大便溏薄、次数增多六月余', '半年来每日大便3-4次，晨起必泄一次，质溏薄，甚或有粘液。食少神疲，有轻度腹痛，便后疼痛稍缓解。曾在外院服药治疗（药名不详），但疗效不佳。今来我处诊治。现症见：大便次数增多，3～4次/日，其中晨起必泄一次，粪质溏薄，甚或有粘液，食少神疲，腹部微痛，泻后则安。 舌质： 舌质稍淡红，体稍胖大。', '泄泻，便粘，纳差，神疲，腹痛', '五更泄', '慢性结肠炎', '中医内科', '15剂,每日一剂,水煎服', '温肾健脾，固肠止泻法', '结肠炎方加减。', '泽泻15g,炒薏仁15g,附子10g,炮姜6g,川朴10g,乌贼骨10g,砂仁6g,白术10g,茯苓15g,猪苓10g,桂枝6g,苍术10g,吴茱萸5g,故纸10g,煨肉豆蔻10g,赤石脂20g,干姜10g,诃子肉10g,甘草3g', ' 饮食清淡，忌生冷辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (185, '李振华滋补心肾，清火平肝法治疗心悸', 2, '侯某', '36', NULL, '男', '20050524', '心悸、气短、胸闷、头晕半年余。', '患者系郑州市郊农民。于半年前突然心悸、胸闷，经心电图检查为心律不齐。在当地诊所服心律平等西药后症状消失。于2004年11月再次出现心慌、气短、头晕，在当地乡村诊所服用中药（具体药物不详）治疗，症状无改善，特来找李老诊治。现心悸不宁，气短，胸闷，头晕，神疲乏力，饮食尚可，二便正常。心律104次/分', '心悸，气短，胸闷，头晕，神疲，乏力', '心悸', '心动过速,', '中医内科', '10剂,每日1剂，分2次口服。水煎；药汁冲服鸡子黄', '滋补心肾，清火平肝法', '加味生脉散,黄连阿胶汤', '白干参15g,寸冬15g,元参12g,生地15g,蒸首乌18g,杞子15g,黄精15g,茯神15g,枣仁15g,节菖蒲10g,栀子10g,黄连5g,阿胶10g,龙齿15g,天麻10g,杜仲10g,钩藤12g,菊花12g,甘草3g,鸡子黄一枚（药水冲服）', '保持情志舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (186, '李振华滋阴益肾，清心安神法治疗不寐', 2, ' 段某', '60', NULL, '男', '20050723', '胃中不适2年余，失眠1月。', ' 患者为汽车司机，因经常不能按时饮食，引起胃中不适，无吐酸，不嗳气，饮食减少。近因装修房子繁忙，操劳过甚，引起失眠。现失眠，入睡困难，多梦，心烦急躁，腰膝酸软，头晕，口干，胃中不适', '失眠，多梦，心烦，急躁，腰酸，头晕，口干，胃中不适', '不寐', '无', '中医内科', '14剂,1日1剂，分2次口服。水煎', '华滋阴益肾，清心安神法', '滋阴清肝饮加减', '蒸首乌18g,白芍15g,杞子15g,山茱萸15g,丹皮10g,炒栀子10g,女贞子15g,五味子10g,桑椹15g,川断15g,知母12g,枣仁15g,菟丝子15g,甘草3g。', ' 注意休息，勿使过劳', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (187, '李振华健脾疏肝，润肠通便法治疗便秘', 2, '张某', '26', NULL, '女', '0', '大便秘结5年余。', ' 患者于5年前即经常出现大便秘结，大便3--5天一行，便干难解经治疗效果不佳。现仍大便干结，已5天未解，脘闷腹胀，食后胃脘胀闷较甚，头晕', '便秘，腹胀，头晕', '便秘', '便秘', '中医内科', '14剂,每日1剂，分2次口服。水煎 ', '健脾疏肝，润肠通便法', '香砂温中汤加减', '白术10g,茯苓15g,陈皮10g,半夏10g,香附10g,砂仁8g,厚朴10g,木香6g,西茴10g,乌药10g,郁金10g,火麻仁18g,草决明15g,郁李仁15g,枳实6g,天麻10g,甘草3g', '保持情志舒畅；可适当活动，避免整日坐卧。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (188, '李振华疏肝理脾，清泄肝火法治疗便秘', 2, ' 李某', '41', NULL, '女', '0', '大便秘结，腹胀二月余', '患者于2月前无明显诱因出现腹胀难忍，大便秘结。2月来大便经常5--7天排便1次，便秘难解。现大便秘结，干燥难解，腹部胀满，心烦急躁，口干，饮食减少，小便正常', '便秘，腹胀，心烦，急躁，口干，纳差', '便秘', '便秘', '中医内科', '7剂,每日1剂，分2次口服。水煎', '疏肝理脾，清泄肝火法', '脏躁方加味', '白术10g,茯苓15g,橘红10g,半夏10g,香附10g,郁金10g,节菖蒲10g,西茴10g,乌药10g,栀子10g,莲子心6g,龙齿20g,夜交藤20g,枳壳10g,甘草3g,合欢皮15g,知母15g,白蔻仁10g,萝卜种20g,火麻仁20g,草决明18g,郁李仁18g,焦山楂12g,焦神曲12g,焦麦芽12g。', '保持情志舒畅；食清淡易消化之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (189, ' 李振华疏肝健脾，清心安神法治疗胁痛', 2, ' 李某', '47', NULL, '女', '20050730', '胁部隐痛加重半年余，伴烦躁，失眠。', ' 患者于60年代出现右胁痛，按压痛甚，时作时止。于80年代做B超检查诊为：1.胆囊炎；2.胆结石。经过治疗，胆结石已排出，而后右胁隐痛不止。于2004年12月份胁部隐痛加重，夜间疼痛更甚，心烦急躁，失眠多梦突出。闻知李老善治疑难杂症特来诊治。现右胁隐痛不适，胆区有压痛，心烦急躁易怒，失眠多梦，经常夜不能寐，两颧潮红，胃时有胀满不舒。', '胁痛，胃胀，心烦，急躁，易怒，失眠，多梦，两红', '胁痛', '慢性胆囊炎,慢性胃炎,', '中医内科', '15剂,每日1剂，分2次口服。水煎 ', '疏肝健脾，清心安神法', '脏躁方加味', '白术10g,茯苓15g,橘红10g,半夏10g,香附10g,郁金10g,西茴10g,乌药10g,栀子15g,莲子心6g,龙齿20g,夜交藤20g,节菖蒲10g,枳壳10g,甘草3g,川楝子12g,元胡10g,青皮10g,厚朴10g。', '保持情志舒畅，忌怒；不能吃辛辣之物如辣椒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (190, '李振华健脾祛湿，清火平肝法治疗眩晕', 2, '刘某', '54', NULL, '男', '20060415', '眩晕10年余，胸闷1年。', '患者于1990年发现高血压，当时血压170/120mmHg，经用西药治疗后缓解，后经常服用西药，血压基本维持正常，但时有眩晕，呈阵发性发作。1年前无明显诱因出现胸闷，眩晕加重，经用中西药治疗（具体不详）未缓解。现眩晕，胸闷，头昏沉，下午眩晕甚，可持续3-4小时，乏力，嗳气，大便溏，日2-3次，胃部怕凉，心烦急躁，经常咽痛上火，睡眠、饮食尚可。血压：145/95mmHg', '眩晕，胸闷，头昏，乏力，嗳气，便溏，心烦，急躁，咽痛', '眩晕', '高血压Ⅱ', '中医内科', '21剂,每日1剂，分2次口服。水煎', '健脾祛湿，清火平肝法', '香砂温中方加味', '白术10g,茯苓15g,陈皮10g,半夏10g,香附10g,砂仁8g,厚朴10g,木香10g,桂枝6g,白芍12g,西茴10g,乌药10g,甘草3g,泽泻18g,炒薏仁30g,丁香5g,柿蒂18g,知母12g,炒栀子10g,天麻10g', '避免进食油腻之物；保持情志舒畅。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (192, '李振华滋阴补肾，清利湿热法治疗淋证', 2, '刘某', '54', NULL, '男', '20060523', '尿不净一年，小便频数两月余', ' 患者自述一年前因房室频繁，出现尿后滴白粘液，尿不净感，未经治疗。于两月前出现会阴部潮湿，小便频、量少，时有色黄，偶有腰酸、腰痛、早泄现象，特来就诊。现症见：尿后白粘液、尿不净、尿分叉、量少，时有色黄，会阴部潮湿，手足心热，偶有腰酸、腰痛、早泄。睡眠时有多梦，大便溏，日两次', '尿不净,尿分叉,尿少,尿黄,阴湿,手足心热,腰酸,腰痛,早泄,多梦,便溏', '淋证', '前列腺炎', '中医内科', ' 10剂,每日一剂,水煎服 ', '滋阴补肾，清利湿热法', '知柏地黄汤化裁', '蒸首乌18g,丹皮10g,杞子15g,黄精15g,盐知母10g,盐黄柏10g,海金砂15g,川断18g,滑石18g,石苇25g,金钱草15g,生苡仁30g,泽泻15g,肉苁蓉12g,甘草3g', '节制房室，多休息，多饮水', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (193, '李振华温中健脾，理气和胃法治疗胃脘痛', 2, '杨某', '41', NULL, '女', '0', '间断性胃脘隐痛八年余', '患者自述于八年前因工作繁忙，饮食不调，饥饱失宜，终致胃病。虽长期间断性服用多种中西药物治疗，但病情时轻时重，反复发作，终未治愈。2005年春节因饮食不当而致病情加重，许昌市中心医院胃镜检查提示：慢性红斑性胃炎。来诊时症见胃脘隐痛，喜暖喜按，腹胀纳差，嗳气，身倦乏力，四肢欠温，大便溏薄，日行2-3次', '胃痛,腹胀,纳差,嗳气,乏力,肢凉,便溏', '胃脘痛', '慢性红斑性胃炎', '中医内科', ' 20剂,每日一剂,水煎服 ', '温中健脾，理气和胃法', '加减香砂六君子汤,', '党参10g,白术10g,茯苓15g,橘红10g,半夏10g,木香6g,砂仁8g,厚朴10g,枳壳10g,桂枝5g,干姜6g,炒薏仁30g,泽泻12g,柿蒂15g,甘草3g。', '忌生冷、油腻食物，勿过劳', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (194, '李振华温中散寒，疏肝和胃法治疗胃脘痛', 2, '刘某', '40', NULL, '男', '20051206', '间断性胃脘疼痛五年余。', '自述于五年前因经常外地出差销售产品，饮食不规律，加之生冷寒凉，导致胃痛，并常因饮酒而诱发，长期交替服用奥克胶囊、复方胃友、乐得胃、雷尼替丁、洛赛克胶囊等药，病情时轻时重。曾在河南中医学院第一附属医院作胃镜诊断为十二指肠球部溃疡。现胃脘部疼痛，痛处喜暖喜按，空腹痛甚，得食痛缓，痛连两胁，嗳气频作，纳差食少，面色萎黄，嘈杂泛酸', '胃痛,胁痛,嗳气,纳差,面黄,嘈杂,泛酸', '胃脘痛', '十二指肠球部溃疡。', '中医内科', '7剂,每日一剂,水煎', '温中散寒，疏肝和胃法', '香砂温中汤加减', '党参15g,白术10g,茯苓12g,吴茱萸6g,干姜8g,桂枝5g,陈皮10g,砂仁6g,半夏10g,香附12g,厚朴10g,乌药10g,木香10g,甘草3g。', ' 忌生冷寒凉并有规律饮食，戒郁怒', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (195, '李振华健脾化湿，通阳宣痹法治疗胸痹', 2, '孙某', '47', NULL, '男', '20050709', '间断性胸闷、气短一年余', '一年前，患者间断性出现胸前憋闷、气短等症状，后因心前区憋闷疼痛难忍，于郑州大学一附院住院诊治，时诊断为冠心病。因心前区疼痛持续时间及程度等呈加重势态，行心脏支架手术（PCI），同年又因心绞痛复发，住院行第二次PCI手术，术后心绞痛等症状好转，血压可控制在120/80mmHg左右。近半年来，又出现胸闷、气短，且有加重趋势，今特请李老诊治。现症见：胸闷，气短，活动后加重，咳痰，色白量多，口干不欲多饮，饮食、二便正常', '胸闷,气短,咯痰,口干', '胸痹', '冠心病,', '中医内科', ' 21剂,每日一剂,水煎', '健脾化湿，通阳宣痹法', '新加瓜蒌薤白汤,香砂温中汤加减', '瓜蒌18g,薤白10g,檀香10g,桂枝5g,白术10g,云苓12g,泽泻18g,白蔻仁10g,荷叶20g,节菖蒲10g,丹参18g,半夏10g,香附10g,砂仁10g,陈皮10g,西茴10g,木香6g,枳壳10g,川朴10g,乌药10g,白芍10g,郁金10g,甘草3g', '戒烟酒，清淡饮食，忌生冷、辛辣油腻之物，食勿过饱。保持心情舒畅，避免劳累。保持大便通畅。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (196, '李振华健脾温中，祛湿止泻法治疗泄泻', 2, '朱某', '52', NULL, '男', '20060503', '腹泻一周余', '患者自述于一周前因过食生冷之物后出现腹泻，服西药黄连素稍好转，三天前又因饮酒后，腹泻又作，服前药减轻。一天前又因食油腻及劳累后，症状加重。现症见腹泻，便质稀薄，色淡黄，日2-4次，腹部隐隐作痛，喜温喜按，体倦乏力，纳差，睡眠尚可', '腹泻，腹痛，乏力，纳差', '泄泻', '急性胃肠炎', '中医内科', ' 10剂,每日一剂,水煎', '健脾温中，祛湿止泻法', '香砂温中汤加减。', '白术10g,茯苓12g,陈皮10g,半夏10g,香附10g,砂仁12g,厚朴10g,桂枝10g,白芍12g,西茴10g,乌药10g,木香6g,郁金10g,甘草3g,苍术10g,泽泻15g,猪苓10g,吴茱萸6g,生姜5片,大枣5枚', '饮食宜清淡，忌食辛辣、生冷等物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (197, '李振华健脾益气，疏肝和胃法治疗胃脘痛', 2, '王某', '57', NULL, '男', '20050321', '间断性胃脘痛十余年', '患者自述于十年前因饮食不节，过食生冷、油腻之品致胃脘隐痛，虽长期服用多种中西药物治疗，但病情时轻时重，反复发作。且每因饮食不调或情志不遂而使病症加重。2005年1月因情志不畅加之饮酒致病情加重，经洛阳市150医院胃镜、胃粘膜组织活检诊断为浅表-萎缩性胃炎。来诊时症见胃脘疼痛连及两胁，腹胀，食后胀甚，嗳气频作，少食，日进食半斤许，大便溏薄，日1-2次，身倦乏力', '胃痛，胁痛，腹胀，嗳气，纳差，便溏，乏力', '胃脘痛', '慢性萎缩性胃炎,', '中医内科', ' 15剂,每日一剂,水煎', '健脾益气，疏肝和胃法', '加减香砂六君子汤', '党参10g,白术10g,茯苓15g,陈皮10g,半夏10g,木香6g,砂仁8g,厚朴10g,枳壳10g,郁金10g,元胡10g,乌药10g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g,生姜3片,大枣3枚', '忌食辛辣、生冷、油腻之品，调畅情志，勿过度劳累', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (198, '李振华健脾豁痰，活血利咽法治疗喉痹,跌后筋脉损伤', 2, '周某', '29', NULL, '男', '20050618', '失音2年余', '患者二年前，感冒后出现音哑，时查CT（2004年3 月16日）诊断提示为：纵膈淋巴瘤。喉镜示：右侧喉上喉返神经联合麻痹（声带：右侧声带旁中线位弧形麻痹）。口服中、西药（药物不详），效差。CT（2005年4 月7日）查示：纵膈淋巴瘤并颈部淋巴结转移。今年元月因摔伤而致臀部、左侧下肢有麻木伴左下肢热辣感，酸软无力，小便不畅。静脉注头孢曲松钠、生脉饮等症状未见明显减轻，今请李老诊治。现症见：音哑，咽部稍干，左侧臀部及下肢麻木，有热辣感，关节酸软无力，小便后心悸', '喑哑，咽干，肢麻，关节软，心悸', '喉痹,跌后筋脉损伤', '1.纵膈淋巴瘤2.外伤后左侧下肢神经肌肉损伤', '中医内科', '15剂,每日一剂,水煎', '健脾豁痰，活血利咽法', '香砂温中方加减', '党参12g,白术10g,茯苓12g,陈皮10g,半夏10g,香附,10g,砂仁10g,厚朴10g,桂枝,10g,白芍,10g,西茴10g,乌药10g,木香6g,郁金10g,甘草3g,知母10g,山甲10g,川断18g,云故纸12g,牛蒡子12g,豆根12g,生苡仁30g,木瓜18g,山山慈菇10g,莪术15g', '调畅情志，清淡饮食，忌贪凉、辛辣油腻之物。注意休息', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (199, ' 李振华疏肝理气，健脾和胃法治疗胃痞', 2, '张某', '33', NULL, '女', '20050702', '腹胀下坠，嗳气一年多加重一周', '患者一年前，体检时B超妇科检查：子宫内多发性肌瘤，恐发癌变，故在郑州市第三职工医院进行子宫全切术，手术后病情恢复良好，共住院两周而后出院，出院后自觉腹胀，自服吗丁啉、普瑞博思等药症状有所缓解，而后腹胀时轻时重，病情反复发作，一周前腹胀下坠感加重,嗳气频繁，食欲不振，既到郑州市中医院诊治，服中药七剂疗效不佳，故前来我院就诊。', '腹胀，嗳气，纳呆', '胃痞', '胃下垂,胃下垂Ⅱ度', '中医内科', '7剂,每日1剂，分2次口服。水煎 ', '疏肝理气，健脾和胃法', '加减香砂六君子汤,', '白术10g,茯苓12g,陈皮10g,半夏10g,香附10g,砂仁8g,厚朴10g,桂枝5g,白芍12g,西茴10g,乌药10g,木香6g,柿蒂15g,丁香6g,桔梗10g,白蔻仁10g,佛手10g,甘松10g,焦山楂12g,焦神曲12g,焦麦芽12g,升麻10g,火麻仁18g,甘草3g', '应少食多餐，饭后尽量少运动', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (200, '李振华活血祛风，燥湿透表法治疗风疹', 2, '郑某', '35', NULL, '男', '20051030', '全身出疹块发痒一周余', '上周晚上外出，天气较寒，回家不久，面部发痒，很快遍及下肢。初起时，身上有些感冒症状，愈抓愈痒，抓后出现风疹块，疹块逐渐扩大，连成大片。当晚即到某医院治疗，医生给抗过敏药物对症治疗（具体用药不祥），服药后瘙痒稍止，疹块缩小。第二天早上又出现疹块、痒而不痛，遇暖后疹块自行消失，一天发作三、四次。服药三天时起时消。瘙痒不止，特转请中医诊治。现症见：四肢均有疹块，皮肤瘙痒，有抓挠痕迹，疹块高于皮肤，其色苍白，遇冷遇风加重。', '全身出疹,瘙痒', '风疹', '荨麻疹', '中医内科', ' 5剂,一日一剂，分两次口服。水煎服', '活血祛风，燥湿透表法', '解表消疹汤', '当归10g,川芎10g,赤芍15g,羌活10g,防风10g,荆芥10g,地肤子15g,苦参10g,白术10g,茯苓15g,苡仁20g,甘草3g,生姜5片', '1、避风寒，注意保温保暖。2、忌生冷、海鲜类食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (201, '李振华益气活血，舒筋通络法治疗中风', 2, '张某', '63', NULL, '男', '20050625', '双下肢行走不便一月余', '患者于1月前无明显诱因，突然感觉行走不便（双腿抬起困难），需在家人搀扶下行走。右手不能灵活写字，记忆力减退，语言不流利，头晕、不痛，无耳鸣，口不苦，痰不多，大便干结，1次/3～4日，皮肤瘙痒。在当地诊所进行治疗（具体诊断及用药不详）效果不明显，故来我院门诊就医。现症见：双下肢行走不便，右手活动欠灵活，头晕，身倦乏力，夜眠差，小便量多，大便干结', '行走不便，头晕，身倦，乏力，寐差，尿多，便干', '中风', '1.陈旧性多发性脑梗塞2.前列腺肥大3.肺气肿', '中医内科', '20剂,每日一剂,水煎 ', '益气活血，舒筋通络法', '补阳还五汤加减', '黄芪30g,党参15g,白术10g,茯苓12g,当归12g,白芍15g,川芎10g,生地15g,香附10g,丹参15g,鸡血藤25g,川牛膝15g,木瓜15g,肉苁蓉15g,火麻仁15g,乌梢蛇12g,甘草3g', '注意休息，调理情志。清淡饮食，忌辛辣油腻之物。适当运动，加强功能锻炼', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (202, '李振华清热平肝，解郁安神法治疗眩晕', 2, '徐某', '49', NULL, '女', '20051008', '头晕间断发作10年余', '患者平素性情急燥，10余年前因与家人生气出现头晕，测血压150/100mmHg，后被诊断为高血压，间断口服维压静治疗，然症状时有时无，近几年头晕发作频繁，时感心前区憋痛，伴后背疼痛，现症见：头晕且胀，耳鸣，遇劳、恼怒加重，时感心前区憋痛，伴肩背疼痛，眠差，食后时有腹胀，纳可，二便正常', '头晕，头胀，耳鸣，胸闷，肩背痛，寐差，腹胀', '眩晕', '高血压病', '中医内科', '30剂,每日一剂,水煎服', '清热平肝，解郁安神法', '脏躁方加减', '土炒白术10g,茯苓12g,橘红10g,旱半夏10g,香附10g,郁金10g,炒栀子7g,节菖蒲10g,莲子心6g,夜交藤25g,丹参15g,砂仁6g,檀香10g,西茴10g,枳壳10g,龙齿10g,天麻10g,厚朴10g,甘草3g,琥珀3g（冲）,朱砂2g（冲）', '畅情志，戒燥怒，忌肥甘辛辣', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (203, '李振华养阴益胃，理气消痞法治疗胃痞', 2, '李某', '58', NULL, '女', '0', '胃脘痞满1年余', '患者于1年前出现胃脘不适，痞闷不舒，嗳气、矢气后稍减，自觉胃内灼热感严重，经胃镜检查显示：红斑性胃炎。经中西医药物治疗效果不明显而来就诊。现胃脘满闷不舒，自觉胃内灼热、烧心、反酸明显，伴有嗳气，纳差食少，稍食即饱，多食则干呕，咽干不渴，入睡困难，手足心热，易上火，大便偏干，2-3天1行', '胃痞，胃热，烧心，泛酸，嗳气，纳差，咽干，寐差，手足心热，便干', '胃痞', '红斑性胃炎', '中医内科', '7剂,每日1剂，分2次口服。水煎 ', '养阴益胃，理气消痞法', '沙参养胃汤加减', '沙参15g,麦冬12g,石斛12g,知母10g,天花粉12g,内金10g,陈皮10g,焦山楂10g,焦神曲10g,焦麦芽10g,郁金10g,乌药10g,萝卜种15g,夜交藤25g,龙齿15g,合欢皮15g,甘草3g。', '饮食清淡，富于营养，易于消化；保持情志舒畅；注意休息。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (204, '李振华健脾疏肝，平肝通窍法治疗耳鸣,眩晕', 2, '马某', '27', NULL, '女', '20050920', '耳鸣，头晕2年余。', '患者2年前无明显原因出现双耳发胀，流脓，左耳鼓膜穿孔，在郑州武警医院、空军医院、郑大医学院诊为中耳炎，口服抗生素、外用滴耳液、口服中药（均不详）治疗，流脓消失，但有耳鸣、头晕至今未除。现耳鸣，两耳时有发胀，头晕不痛，时有心烦急躁，乏力，饮食、睡眠、二便均尚可', '耳鸣，耳胀，头晕，心烦，急躁，乏力', '耳鸣,眩晕', '慢性中耳炎,', '中医内科', '14剂,每日1剂，分2次口服。水煎 ', '健脾疏肝，平肝通窍法', '加减香砂六君子汤', '白术10g,茯苓15g,陈皮10g,半夏10g,香附10g,砂仁8g,木香10g,桂枝3g,白芍12g,西茴10g,乌药10g,甘草3g,灵磁石30g,蝉衣10g,天麻10g,细辛5g,佛手10g,菊花10g。', '忌食辛辣、肥甘之物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (205, '李振华健脾和胃，疏肝理气法治疗胃痞', 2, '许某', '42', NULL, '男', '20060509', '胃脘胀满17年，加重20天', '患者于1989年因饮食不节致胃脘胀满，不能进食，在解放军371医院作胃镜检查，显示贲门轻度糜烂、十二指肠溃疡。服中药3个月余，症状消失。以后每因饮酒可致胃脘胀满发作，服藿香正气丸、保和丸症状可缓解，食欲尚可，但饭后胃脘胀满。20天前因饮酒和进食硬食物后致胃脘胀满加重。现胃脘胀满，食后加重，胃不痛，不烧心，不反酸，大便稀溏，日2-3次，稍有不畅，粘液便，饮食、睡眠尚可。', '胃胀，便溏，粘液便', '胃痞', '慢性胃炎', '中医内科', '10剂,每日1剂，分2次口服。水煎', '健脾和胃，疏肝理气法', '香砂温中汤加减', '白术10g,茯苓15g,陈皮10g,半夏10g,香附10g,砂仁8g,厚朴10g,木香10g,西茴10g,乌药10g,桂枝6g,白芍12g,甘草3g,萝卜种15g,知母12g,刘寄奴15g,香橼10g,焦山楂10g,焦神曲10g,焦麦芽10g,泽泻15g,炒薏仁25g', '治疗期间禁酒；忌食生冷、肥甘、油腻之品', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (206, '李振华健脾疏肝，和胃制酸法治疗胃脘痛', 2, '刘某', '26', NULL, '男', '20060316', '胃痛，吐酸10年余', '患者于1996年因吸烟引起胃中疼痛，泛吐酸水，长期未愈。于2005年12月在郑州市第五人民医院胃镜检查诊为：1.慢性食管炎；2.慢性浅表性胃炎；3.十二指肠球部溃疡；HP（+）。经用中西医药（不详）治疗效果不佳，而来找李老诊治。现胃脘疼痛，泛吐酸水，饮食正常，吸烟多时口苦，睡眠尚可，二便正常。', '胃痛，泛酸，口苦', '胃脘痛', '慢性胃炎,十二指肠溃疡,', '中医内科', '7剂,每日1剂，分2次口服。水煎', '健脾疏肝，和胃制酸法', '加减香砂六君子汤,左金丸', '白术10g,茯苓15g,陈皮10g,山药20g,香附10g,砂仁10g,厚朴10g,枳壳10g,郁金10g,乌药10g,元胡10g,焦山楂12g,焦神曲12g,焦麦芽12g,刘寄奴12g,黄连5g,吴茱萸3g,甘草3g,生姜10g。', '忌食辛辣、油腻之物。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (207, '李振华温中散寒、行气活瘀法治疗胃脘痛', 2, '王某', '25', NULL, '女', '20050120', '间断性胃脘疼痛三余年。', '患者自述间断性胃脘疼痛已三余年，疼痛时自服西药以求缓解。近两个月来疼痛频繁，程度日益加重，每因饮食不温、学习紧张而痛发。2004年12月3日经河南中医学院第一附属医院胃镜确诊为浅表性胃炎。现胃脘疼痛，间有刺痛，疼处喜暖，腹胀、嘈杂、食后胀甚、嗳气、少食，大便溏薄，身倦乏力', '胃痛，腹胀，嘈杂，嗳气，纳差，便溏，乏力', '胃脘痛', '慢性浅表性胃炎,', '中医内科', '7剂,口服,水煎', '温中散寒、行气活瘀法', '香砂温中汤加减', '党参15g,白术12g,茯苓18g,桂枝10,g,干姜10,g,陈皮10g,半夏10g,木香10g,砂仁,8g,郁金12g,刘寄奴15g,元胡10g,川芎10g,炙甘草5g。', '忌生冷及不易消化食物', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (208, '李振华温肾健脾，助阳化气法治疗纳呆', 2, '郭某 ', '50', NULL, '男', '20050716', '食无味，不知饥饿6月余', '6月前无明显诱因出现饮食无味，不知饥饿，先后服用人参鹿茸片等效果不佳。在郑州市某家医院确诊为“浅表性胃炎”，未给予正规治疗，今特求李老诊治。现症见：纳差，饮食无味，不知饥饿，怕冷，小便清长，大便溏薄不成形。', '纳差，畏冷，小便清长，便溏', '纳呆', '浅表性胃炎。', '中医内科', '14剂,每日一剂,水煎', '温肾健脾，助阳化气法', '健脾温肾方', '白术10g,茯苓15g,炒苡仁30g,泽泻15g,苍术10g,厚朴10g,吴茱萸5g,故纸10g,桂枝6g,白芍12g,乌药10g,焦山楂12g,焦神曲12g,焦麦芽12g,甘草3g。', '清淡饮食，忌生冷、辛辣油腻之物，戒烟酒。保持心情舒畅，适度体育锻炼', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (209, '李振华养阴清热，化痰止咳法治疗咳嗽', 2, '谢某', '44', NULL, '男', '20051105', '咳嗽七年，加重一月余', ' 患者自述于七年前因受凉感冒后出现咳嗽，后经自行服药症状好转。后每遇天气转凉或冷空气后咳嗽发作，曾在某医院拍胸片检查未发现异常，遂诊断为慢性咽炎，服西药治疗症状稍缓解。但每遇冷后，即反复发作。一月前，因天气转冷受凉，咳嗽又作，现症见咳嗽，痰少，遇凉咳甚，口干，咽部不适，如有物梗阻，纳食不香，眠寐尚可，大便稍干。', '咳嗽，口干，咽梗，纳差，便干', '咳嗽', '咳嗽,慢性咽炎', '中医内科', '7剂,每日一剂,水煎 ', '养阴清热，化痰止咳法', '清金化痰汤加减', '辽沙参18g,前胡10g,黄芩10g,杏仁10g,瓜蒌仁10g,知母12g,川贝母10g,苏子10g,桔梗10g,枳壳8g,炙紫菀12g,橘红10g,牛蒡子10g,豆根12g,甘草3g。', '注意保暖；饮食清淡；保持情志舒畅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (210, ' 李振华健脾补肺益卫，宣肺祛邪通窍法治疗鼻渊', 2, '苗某 ', '58', NULL, '男', '20050902', '间断性打喷嚏、流鼻涕6年余。', '6年前，无明显诱因始出现打喷嚏、流鼻涕，呈清稀状，不伴有发热、头疼等。每逢感冒或天气转凉而复发，平素易感冒。多次求治于西医治疗，按“鼻炎”曾服用大量西药（扑尔敏等），效果不明显。今特请李老诊治。现症见：咽喉部疼痛，打喷嚏，流清鼻涕，面色萎黄，气短乏力，动则尤甚，饮食可，二便正常。', '咽痛，喷嚏，清涕，面黄，气短，乏力', '鼻渊', '鼻炎', '中医内科', '14剂,每日一剂,水煎 ', '健脾补肺益卫，宣肺祛邪通窍法', '通窍鼻渊方加减', '黄芪25g,西洋参10g,苍耳子10g,辛夷10g,薄荷6g,桔梗10g,白术12g,川牛膝15g,焦山楂12g,焦神曲12g,焦麦芽12g,木瓜18g,茯苓15g,生苡仁30g,白芍15g,陈皮10g,砂仁8g,防风4g,甘草3g ', ' 清淡饮食，忌生冷、辛辣油腻之物。注意避风保暖，预防感冒。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
INSERT INTO `myya_ya` VALUES (211, ' 崔公让清热养阴活血化瘀法治疗脱疽', 3, ' 王某', '54', NULL, '男', '20060108', '右足拇指溃破，疼痛一周.', ' 患者于3年前,出现双下肢发凉，长时间步行后出现左下肢酸困疼痛休息后可以缓解，后渐加重。曾在当地以“血管炎”治疗，效果欠佳。现步行100米即出现酸困不适。于一周前出现右足拇指趾端溃破，疼痛剧烈。', '右足拇指趾端潮红,发绀，溃破，轻度肿胀。疼痛剧烈。步行100米即出现酸困不适。脉细数，舌质红，苔黄燥。', '脱疽', '动脉硬化闭塞症伴感染（缺血坏死Ⅰ级坏死）', '中医内科', '15,1,水煎服', '清热养阴活血化瘀法', '四妙活血汤,四妙活血汤', '当归20g,丹参30g,鸡血藤30g,二花30g,元参30g,麦冬20g,石斛20g,炮山甲12g,甘草10g', ' 严格戒烟，患肢保暖，保持伤面清洁。避风寒，畅情志，禁用过热开水烫洗，劳逸适度。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0);
